		 nr_documento,
		 dt_emissao,
		 dt_aviso,
		 vl_saldo,
		 dt_contabilizacao,
		 cd_conta_contabil,
		 vl_saldo_orig,
		 vl_imposto,
		 nr_seq_segurado,
		 ie_tipo_contratacao,
		 ie_segmentacao,
		 nr_seq_contrato,
		 dt_ocorrencia,
		 nr_seq_pagador,
		 nr_seq_prestador,
		 ie_tipo_segurado,
		 nr_protocolo_ans,
		 ie_tipo_documento,
		 ie_tipo_grupo_ans,
		 ie_preco,
		 nr_seq_congenere,
		 dt_mes_competencia
	order by nr_titulo;

