	or (t.dt_liquidacao > dt_final_w)) and coalesce(cd_estabelecimento_p,t.cd_estabelecimento) = t.cd_estabelecimento and obter_empresa_estab(t.cd_estabelecimento) = cd_empresa_w and t.vl_titulo > 0 and obter_saldo_titulo_pagar(t.nr_titulo,dt_final_w) <> 0 and l.dt_fechamento	<= dt_final_w and ((ie_gerar_ind_classif_w <> 'N')
	or	m.cd_conta_cred   in (select c.cd_conta_contabil
