	or 	coalesce(vet_titulo_ress_sus.nr_seq_processo::text, '') = '') and c.nr_seq_proc_gru	= vet_titulo_ress_sus.nr_seq_proc_gru and ((coalesce(ie_tipo_segurado_w, 'X')	= coalesce(s.ie_tipo_segurado, 'X'))
