from	ctb_livro_auxiliar	a
where	a.nr_sequencia	= nr_seq_reg_auxiliar_p;

nr_contador_w	:= 0;

cd_empresa_w	:= obter_empresa_estab(cd_estabelecimento_p);

select	coalesce(max(a.ie_data_tipo_segurado),'A')
into STRICT	ie_data_tipo_segurado_w
from	pls_parametros a
where	a.cd_estabelecimento = cd_estabelecimento_p;

select	substr(obter_desc_expressao(922923),1,255)
into STRICT	ds_observacao_w
;

delete
from	pls_aux_event_liq a
where	a.nr_seq_reg_auxiliar	= nr_seq_reg_auxiliar_p;

commit;

vl_acumulado_w	:= 0;
nr_titulo_ant_w := 0;
qt_registros_w 	:= 0;

open c_titulo_conta;
loop
fetch c_titulo_conta into	
	vet_titulo_conta;
EXIT WHEN NOT FOUND; /* apply on c_titulo_conta */
	begin
	
	open c_itens_conta;
	loop
	fetch c_itens_conta into
		vet_itens_conta;
	EXIT WHEN NOT FOUND; /* apply on c_itens_conta */
		begin
		
		if (ie_data_tipo_segurado_w = 'A') then
			dt_ref_repasse_w	:= vet_itens_conta.dt_ocorrencia;
		else
			dt_ref_repasse_w	:= vet_itens_conta.dt_mes_competencia;
		end if;
		
		SELECT * FROM pls_obter_dados_repasse(	dt_ref_repasse_w, vet_itens_conta.nr_seq_segurado, vet_itens_conta.nr_seq_congenere, ie_tipo_repasse_w, ie_tipo_compartilhamento_w, dt_repasse_w, dt_fim_repasse_w) INTO STRICT ie_tipo_repasse_w, ie_tipo_compartilhamento_w, dt_repasse_w, dt_fim_repasse_w;
		
		ie_tipo_documento_w	:= 'CM';
		
		nr_contador_w		:= nr_contador_w + 1;
		
		qt_classif_diops_w 	:= dt_final_w - vet_titulo_conta.dt_vencimento;

		if	((obter_dias_entre(vet_itens_conta.dt_aviso, dt_final_w) + 1) > 30) then
			ie_classif_pel_w	:= '1';
		else
			ie_classif_pel_w	:= '2';
		end if;

		if (qt_classif_diops_w > 120) then
			ie_classif_diops_w	:= '5';
		elsif (qt_classif_diops_w > 90) then
			ie_classif_diops_w	:= '4';
		elsif (qt_classif_diops_w > 60) then
			ie_classif_diops_w	:= '3';
		elsif (qt_classif_diops_w > 30) then
			ie_classif_diops_w	:= '2';
		elsif (qt_classif_diops_w > 0) then
			ie_classif_diops_w	:= '1';
		else
			ie_classif_diops_w	:= '0';
		end if;
		
		ds_observacao_item_w := null;
		
		if (vet_itens_conta.ie_tipo_segurado in ('C','I','T')) then
			ds_observacao_item_w	:= ds_observacao_w;
		end if;

		insert into pls_aux_event_liq(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_reg_auxiliar,
			nr_seq_prestador,
			nr_titulo,
			nr_seq_lote_pgto,
			dt_vencimento,
			cd_conta_contabil,
			nr_documento,
			ie_tipo_documento,
