			ie_tipo_contratacao,
			ie_tipo_segmentacao,
			dt_aviso,
			dt_ocorrencia,
			dt_emissao,
			nr_seq_segurado,
			nr_seq_pagador,
			vl_evento,
			vl_saldo,
			dt_contabil,
			ie_classif_pel,
			ie_classif_diops,
			nr_seq_conta,
			ie_tipo_relacao,
			ie_tipo_segurado,
			nr_registro_produto,
			ie_tipo_evento,
			ds_observacao,
			ie_preco,
			ie_tipo_compartilhamento,
			ie_tipo_repasse,
			dt_inicio_compartilhamento,
