CREATE OR REPLACE PROCEDURE pls_gerar_glosa_via_acesso ( nr_seq_conta_p bigint, ie_tipo_consiste_p text, nr_seq_prestador_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar glosa 1899 para obrigatoriedade de via de acesso,.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_conta_proc_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta_proc
	where	nr_seq_conta 		= nr_seq_conta_p
	and	coalesce(ie_via_acesso::text, '') = ''
	and	ie_via_obrigatoria 	= 'S'
	and	coalesce(nr_seq_regra_via_acesso::text, '') = '';
BEGIN
for r_c01_w in C01() loop
	begin
	CALL pls_gravar_conta_glosa(	'1899','', r_c01_w.nr_sequencia, null,'N',
				'Via de acesso não informada ou diferente da via exigida pela operadora',
				nm_usuario_p,'A',ie_tipo_consiste_p,nr_seq_prestador_p,
				cd_estabelecimento_p, '', null);
	end;
end loop;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
