CREATE OR REPLACE PROCEDURE pls_gerar_grupo_contrato ( nr_seq_contrato_p bigint, ie_commit_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_cgc_estipulante_w	varchar(14);
ds_razao_social_w	pessoa_juridica.ds_razao_social%type;
dt_reajuste_w		timestamp;
nr_seq_grupo_w		bigint;
qt_grupo_vinculado_w	bigint;


BEGIN

select	count(1)
into STRICT	qt_grupo_vinculado_w
from	pls_contrato_grupo
where	nr_seq_contrato	= nr_seq_contrato_p;

if (qt_grupo_vinculado_w = 0) then
	begin
	select	cd_cgc_estipulante,
		coalesce(dt_reajuste,dt_contrato)
	into STRICT	cd_cgc_estipulante_w,
		dt_reajuste_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_p;
	exception
	when others then
		cd_cgc_estipulante_w	:= null;
	end;
	
	if (cd_cgc_estipulante_w IS NOT NULL AND cd_cgc_estipulante_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_grupo_w
		from	pls_grupo_contrato
		where	cd_cgc	= cd_cgc_estipulante_w
		and	ie_tipo_relacionamento = '1';
		
		if (coalesce(nr_seq_grupo_w::text, '') = '') then
			select	ds_razao_social
			into STRICT	ds_razao_social_w
			from	pessoa_juridica
			where	cd_cgc	= cd_cgc_estipulante_w;
			
			select	nextval('pls_grupo_contrato_seq')
			into STRICT	nr_seq_grupo_w
			;
			
			insert	into	pls_grupo_contrato(	nr_sequencia, ds_grupo, cd_estabelecimento, ie_tipo_relacionamento,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					cd_cgc, dt_reajuste, ie_tipo_grupo, ie_situacao)
				values (	nr_seq_grupo_w, ds_razao_social_w, cd_estabelecimento_p, '1',
					clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
					cd_cgc_estipulante_w, null, 'C', 'A');
		end if;
		
		CALL pls_vincular_contrato_grupo(nr_seq_contrato_p, nr_seq_grupo_w, nm_usuario_p, cd_estabelecimento_p);
	end if;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
