SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_guia_req_web ( nr_seq_requisicao_p bigint, ie_tipo_guia_p bigint, ie_origem_p bigint, cd_procedimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_prestador_exec_p bigint, nr_seq_maquina_p bigint, id_maquina_p text, nr_seq_usuario_web_p bigint, ie_executa_consulta_p text, ie_consulta_urgencia_p text, nr_seq_guia_p INOUT bigint, cd_guia_p INOUT text ) AS $body$
DECLARE

	 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade:  Gerar guia de requisição no portal web. 
---------------------------------------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [ ] Relatórios [ ] Outros: 
 ---------------------------------------------------------------------------------------------------------------------------------------------------- 
Pontos de atenção:  
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ 
 
ie_lanc_proc_auto_w			varchar(2);
ie_estagio_w				smallint;
nr_seq_prestador_exec_w			bigint;
cd_guia_w				varchar(20) 	:= '';
nr_seq_guia_w				bigint	:= null;
ie_exec_req_aut_w			varchar(1);
nr_seq_perfil_web_w			pls_usuario_web.nr_seq_perfil_web%type;
vl_parametros_w				varchar(4000);
ie_tipo_consist_req_w			varchar(1)	:= '1';
ie_tipo_atendimento_w			pls_requisicao.ie_tipo_atendimento%type;
cd_versao_tiss_w			pls_versao_tiss.cd_versao_tiss%type;
nr_seq_anexo_w				pls_lote_anexo_guias_aut.nr_sequencia%type;
ie_executa_consulta_w			varchar(1)	:= 'S';
	

BEGIN 
 
begin 
	select	nr_seq_perfil_web 
	into STRICT	nr_seq_perfil_web_w 
	from	pls_usuario_web 
	where	nr_sequencia	= nr_seq_usuario_web_p;
exception 
when others then 
	nr_seq_perfil_web_w := null;
end;
 
/* Parâmetros da função OPSW - Requisições */
 
vl_parametros_w := pls_obter_parametros_funcao(null, null, nr_seq_usuario_web_p, cd_estabelecimento_p, 1289, null, 'P', null, nr_seq_perfil_web_w, null, vl_parametros_w);
 
/* Guia de Consulta */
 
if (ie_tipo_guia_p = 3) then 
	ie_lanc_proc_auto_w := pls_inserir_proc_req_web(null, nr_seq_requisicao_p, null, null, 1, 'P', cd_estabelecimento_p, nm_usuario_p, 'N', null, ie_lanc_proc_auto_w, null);
 
	if (ie_lanc_proc_auto_w = 'S') then 
		CALL pls_consistir_requisicao(nr_seq_requisicao_p,cd_estabelecimento_p,nm_usuario_p);
		 
		select	ie_estagio,           	 
			nr_seq_prestador_exec 
		into STRICT	ie_estagio_w, 
			nr_seq_prestador_exec_w 
		from	pls_requisicao 
        where 	nr_sequencia = nr_seq_requisicao_p;
		 
		ie_executa_consulta_w	:= substr(pls_obter_valor_parametro(21, vl_parametros_w),1,1); -- parâmetro [21] - Executar a requisição de consulta no momento que o usuário solicitá-la 
		
		if (ie_estagio_w = 2 and coalesce(ie_executa_consulta_w, 'S') = 'S') then 
			SELECT * FROM pls_executar_requisicao_web( nr_seq_requisicao_p, null, nr_seq_prestador_exec_w, null, 'P', nm_usuario_p, cd_estabelecimento_p, 'N', 'EP', nr_seq_maquina_p, id_maquina_p, nr_seq_usuario_web_p, nr_seq_guia_w, cd_guia_w) INTO STRICT nr_seq_guia_w, cd_guia_w;
		end if;
	end if;
/* Guia SP/SADT  */
 
elsif (ie_tipo_guia_p = 2) then 
	ie_lanc_proc_auto_w := pls_inserir_proc_req_web(null, nr_seq_requisicao_p, ie_origem_p, cd_procedimento_p, 1, 'P', cd_estabelecimento_p, nm_usuario_p, 'N', null, ie_lanc_proc_auto_w, null);
	 
	ie_tipo_consist_req_w	:= substr(pls_obter_valor_parametro(17, vl_parametros_w),1,1); -- parâmetro [17] - Forma de consistir a requisição e seus itens	 
	if (ie_tipo_consist_req_w = '1') then
		CALL pls_consistir_requisicao(nr_seq_requisicao_p,cd_estabelecimento_p,nm_usuario_p);
	elsif (ie_tipo_consist_req_w = '2' and ie_consulta_urgencia_p = 'S' and ie_lanc_proc_auto_w = 'S') then 
	-- Se o tipo de consistência for 2, só consisti se a requisição for executada automaticamente 
		CALL pls_consistir_requisicao(nr_seq_requisicao_p,cd_estabelecimento_p,nm_usuario_p);
	end if;
		 
	if (ie_lanc_proc_auto_w = 'S' and ie_consulta_urgencia_p = 'S') then 
		select	ie_estagio,           	 
			nr_seq_prestador_exec 
		into STRICT	ie_estagio_w, 
			nr_seq_prestador_exec_w 
		from	pls_requisicao 
		where 	nr_sequencia = nr_seq_requisicao_p;
		 
		if (ie_estagio_w = 2) then 
			SELECT * FROM pls_executar_requisicao_web( nr_seq_requisicao_p, null, nr_seq_prestador_exec_p, null, 'P', nm_usuario_p, cd_estabelecimento_p, 'N', 'EP', nr_seq_maquina_p, id_maquina_p, nr_seq_usuario_web_p, nr_seq_guia_w, cd_guia_w) INTO STRICT nr_seq_guia_w, cd_guia_w;
		end if;
	else 
		cd_versao_tiss_w	:= pls_obter_versao_tiss;
		 
		select	ie_tipo_atendimento 
		into STRICT	ie_tipo_atendimento_w 
		from 	pls_requisicao 
		where	nr_sequencia	= nr_seq_requisicao_p;
		 
		-- Quando o tipo de atendimento for Radioterapia e não for requisição de urgência, deve gerar o cabeçalho do anexo. 
		if (cd_versao_tiss_w >= '3.03.00') and (ie_tipo_atendimento_w = '9') then 
			nr_seq_anexo_w := pls_gerar_cab_anexo_req_web(nr_seq_requisicao_p, 'RA', nm_usuario_p, nr_seq_anexo_w);
		end if;
	end if;
/* Guia de internação */
 
elsif (ie_tipo_guia_p = 1) then 
	ie_lanc_proc_auto_w := pls_inserir_proc_req_web(null, nr_seq_requisicao_p, null, null, 1, 'P', cd_estabelecimento_p, nm_usuario_p, 'N', null, ie_lanc_proc_auto_w, null);
	 
	ie_tipo_consist_req_w	:= substr(pls_obter_valor_parametro(17, vl_parametros_w),1,1); -- parâmetro [11] - Forma de consistir a requisição e seus itens	 
	-- OS 929503 - Verificando parâmetro 33 da função OPSW - Requisições para autorização: se o valor for 'S' então o sistema executa a requisição manualmente mantendo o comportamento padrão do sistema.	 
	ie_exec_req_aut_w := substr(pls_obter_valor_parametro(33, vl_parametros_w),1,1); -- [33]- Permite executar a requisição de internação diretamente, quando possuir regra de lançamento automático. 
	
	if (ie_tipo_consist_req_w = '1') then 
		CALL pls_consistir_requisicao(nr_seq_requisicao_p,cd_estabelecimento_p,nm_usuario_p);
	elsif (ie_tipo_consist_req_w = '2' and ie_exec_req_aut_w = 'S' and ie_lanc_proc_auto_w = 'S') then 
		-- Se o tipo de consistência for 2, só consisti se a requisição for executada automaticamente 
		CALL pls_consistir_requisicao(nr_seq_requisicao_p,cd_estabelecimento_p,nm_usuario_p);
	end if;
	 
	if (ie_lanc_proc_auto_w = 'S') then 
		select	ie_estagio,           	 
			nr_seq_prestador_exec 
		into STRICT	ie_estagio_w, 
			nr_seq_prestador_exec_w 
		from	pls_requisicao 
		where 	nr_sequencia = nr_seq_requisicao_p;
				 
		if (ie_estagio_w = 2 and ie_exec_req_aut_w = 'S') then 
			SELECT * FROM pls_executar_requisicao_web( nr_seq_requisicao_p, null, nr_seq_prestador_exec_w, null, 'P', nm_usuario_p, cd_estabelecimento_p, 'N', 'EP', nr_seq_maquina_p, id_maquina_p, nr_seq_usuario_web_p, nr_seq_guia_w, cd_guia_w) INTO STRICT nr_seq_guia_w, cd_guia_w;
		end if;
	end if;
end if;
 
nr_seq_guia_p	:= nr_seq_guia_w;
cd_guia_p	:= cd_guia_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_guia_req_web ( nr_seq_requisicao_p bigint, ie_tipo_guia_p bigint, ie_origem_p bigint, cd_procedimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_prestador_exec_p bigint, nr_seq_maquina_p bigint, id_maquina_p text, nr_seq_usuario_web_p bigint, ie_executa_consulta_p text, ie_consulta_urgencia_p text, nr_seq_guia_p INOUT bigint, cd_guia_p INOUT text ) FROM PUBLIC;

