CREATE OR REPLACE PROCEDURE pls_gerar_historico_atend ( qt_tempo_p bigint, nr_seq_atendimento_p bigint, ie_tipo_historico_p text, nm_usuario_receb_p text, nm_usuario_envio_p text, ds_menssagem_p text) AS $body$
DECLARE


/*
E - envio
A - aceito
R - recusado
*/
ds_usuario_receb_w	varchar(255);
ds_usuario_envio_w	varchar(255);
ds_historico_w		varchar(500);
nm_usuario_w		varchar(255);


BEGIN

select	ds_usuario
into STRICT	ds_usuario_receb_w
from	usuario
where	nm_usuario = nm_usuario_receb_p;

select	ds_usuario
into STRICT	ds_usuario_envio_w
from	usuario
where	nm_usuario = nm_usuario_envio_p;

nm_usuario_w  := nm_usuario_receb_p;

if (ie_tipo_historico_p = 'E') then
	ds_historico_w 	:= 'O operador ' || ds_usuario_envio_w || ' direcionou o atendimento para o operador ' || ds_usuario_receb_w;
	nm_usuario_w 	:= nm_usuario_envio_p;
elsif (ie_tipo_historico_p = 'A') then
	ds_historico_w := 'O operador ' || ds_usuario_receb_w || ' aceitou o direcionamento do atendimento.';
else
	ds_historico_w := 'O operador ' || ds_usuario_receb_w || ' recusou o direcionamento do atendimento. ' || ds_menssagem_p;
end if;

insert into pls_atendimento_historico( nr_sequencia, nr_seq_atendimento, ds_historico_long,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
	nm_usuario_nrec, nr_seq_tipo_historico, dt_historico,
	qt_tempo_direcionamento)
values (nextval('pls_atendimento_historico_seq'), nr_seq_atendimento_p, ds_historico_w,
	clock_timestamp(), nm_usuario_w, clock_timestamp(),
	nm_usuario_w, 2, clock_timestamp(),
	qt_tempo_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
