SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_hist_faq_atend ( nr_seq_atendimento_p bigint, nr_seq_pergunta_p bigint, nr_seq_resposta_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_pergunta_w			varchar(255);
ds_resposta_w			varchar(3745);
nr_tipo_historico_w		bigint;
qtd_resposta_long_w             bigint;
nr_atendimento_historico_w      bigint;


BEGIN
if (pls_obter_se_controle_estab('GA') = 'S') then
	select	substr(ds_pergunta,1,255)
	into STRICT	ds_pergunta_w
	from	pls_pergunta_atendimento
	where	nr_sequencia = nr_seq_pergunta_p
	and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento );
else
	select	substr(ds_pergunta,1,255)
	into STRICT	ds_pergunta_w
	from	pls_pergunta_atendimento
	where	nr_sequencia = nr_seq_pergunta_p;
end if;

select	substr(ds_resposta,1,3745)
into STRICT	ds_resposta_w
from	pls_resp_pergunta_atend
where	nr_sequencia = nr_seq_resposta_p;

select 	count(*)
into STRICT 	qtd_resposta_long_w
from   	pls_resp_pergunta_atend
where 	nr_sequencia = nr_seq_resposta_p
and 	(ds_resposta_long IS NOT NULL AND ds_resposta_long::text <> '');

if (pls_obter_se_controle_estab('GA') = 'S') then
	select	max(nr_sequencia)
	into STRICT	nr_tipo_historico_w
	from	pls_tipo_historico_atend
	where	ie_historico_faq = 'S'
	and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento );

	if (coalesce(nr_tipo_historico_w::text, '') = '') then
		select	min(nr_sequencia)
		into STRICT	nr_tipo_historico_w
		from	pls_tipo_historico_atend
		where (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento );
	end if;
else
	select	max(nr_sequencia)
	into STRICT	nr_tipo_historico_w
	from	pls_tipo_historico_atend
	where	ie_historico_faq = 'S';

	if (coalesce(nr_tipo_historico_w::text, '') = '') then
		select	min(nr_sequencia)
		into STRICT	nr_tipo_historico_w
		from	pls_tipo_historico_atend;
	end if;
end if;

insert	into pls_atendimento_historico(nr_sequencia, nr_seq_atendimento, ds_historico_long,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
	nm_usuario_nrec, nr_seq_tipo_historico, dt_historico,
	ie_origem_historico, ie_gerado_sistema)
values (nextval('pls_atendimento_historico_seq'), nr_seq_atendimento_p, ds_pergunta_w || chr(10) || ds_resposta_w,
	clock_timestamp(), nm_usuario_p, clock_timestamp(),
	nm_usuario_p, nr_tipo_historico_w, clock_timestamp(),
	'', 'N');

if (qtd_resposta_long_w > 0) then
	select	nextval('pls_atendimento_historico_seq')
	into STRICT	nr_atendimento_historico_w
	;

	insert	into pls_atendimento_historico(nr_sequencia, nr_seq_atendimento, ds_historico_long,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, nr_seq_tipo_historico, dt_historico,
		ie_origem_historico, ie_gerado_sistema)
	values (nr_atendimento_historico_w, nr_seq_atendimento_p, '',
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p, nr_tipo_historico_w, clock_timestamp(),
		'', 'N');

	CALL copia_campo_long_de_para('pls_resp_pergunta_atend',
				'ds_resposta_long',
				' where nr_sequencia = :nr_sequencia',
				'nr_sequencia='||nr_seq_resposta_p,
				'pls_atendimento_historico',
				'ds_historico_long',
				' where nr_sequencia = :nr_sequencia',
				'nr_sequencia='||nr_atendimento_historico_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_hist_faq_atend ( nr_seq_atendimento_p bigint, nr_seq_pergunta_p bigint, nr_seq_resposta_p bigint, nm_usuario_p text) FROM PUBLIC;

