CREATE OR REPLACE PROCEDURE pls_gerar_hist_liminar_benef ( nr_seq_guia_p bigint, nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Identificar se o beneficiário possui liminar judicial e inserir um registro em pls_guia_plano_historico
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_liminar_w	bigint;
ds_liminar_w		varchar(255);
ds_observacao_w		varchar(4000);
qt_liminares_w		bigint;
C01 CURSOR FOR
	SELECT	nr_sequencia,
		ds_liminar
	from	processo_judicial_liminar a
	where	a.nr_seq_segurado 	= nr_seq_segurado_p
	and	trunc(clock_timestamp()) between trunc(dt_inicio_validade) and (coalesce(dt_fim_validade,clock_timestamp()))
	and 	a.ie_estagio	= 2 /* somente os processos liberados*/
	order by nr_sequencia;


BEGIN
ds_observacao_w	:= 'Beneficiário com processo judicial: ';
qt_liminares_w	:= 0;
open C01;
loop
fetch C01 into
	nr_seq_liminar_w,
	ds_liminar_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		qt_liminares_w	:= qt_liminares_w + 1;
		ds_observacao_w := substr((ds_observacao_w || chr(10) || nr_seq_liminar_w || ' - ' ||ds_liminar_w),1,4000);
	end;
end loop;
close C01;

if (qt_liminares_w > 0) then
	insert into pls_guia_plano_historico(nr_sequencia, nr_seq_guia, ie_tipo_log,
		dt_historico, dt_atualizacao, nm_usuario,
		ds_observacao, ie_tipo_historico, IE_ORIGEM_HISTORICO)
	values (	nextval('pls_guia_plano_historico_seq'), nr_seq_guia_P, null,
		clock_timestamp(), clock_timestamp(), nm_usuario_p,
		ds_observacao_w,'L', 'A');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
