SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_import_mat_a900_new ( nr_seq_lote_imp_p bigint, nm_arquivo_p text, nm_usuario_p text ) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
 Goal: Insert the materials records on the table W_PLS_MATERIAL_UNIMED
 ----------------------------------------------------------------------------------------------------
 Direct call locations:
 [   ] Dictionary objects [ X ] Tasy (Delphi/Java) [   ] Portal [    ] Reports [   ] Others:
 ---------------------------------------------------------------------------------------------------
 Attention points: Made by lots
 ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_local_w              varchar(255);
ds_erro_w               varchar(4000);
ie_leitura_w            boolean := true;
ds_linha_w              varchar(4000);
ie_tipo_w               varchar(3);
qt_registro_w           integer := 0;
nr_versao_ptu_w         pls_lote_imp_mat_unimed.nr_versao_ptu%type;
ie_tipo_arquivo_imp_w   pls_lote_imp_mat_unimed.ie_tipo_arquivo_imp%type;
ie_tipo_arquivo_w       pls_lote_imp_mat_unimed.ie_tipo_arquivo%type;
ie_versao_w             bigint;
ie_tipo_pai_w           varchar(3);
qt_linha_filha_w        integer;
nr_seq_mat_unimed_w     w_pls_material_unimed.nr_sequencia%type;
nm_material_w           w_pls_material_unimed.nm_material%type;
ds_arquvio_w            w_pls_mat_unimed_xml.ds_arquivo%type;




BEGIN

-- Update the import lot
update	pls_lote_imp_mat_unimed
set	dt_inicio_importacao = clock_timestamp(),
	nm_usuario_imp = nm_usuario_p,
	ie_status = '2',
	ds_log  = NULL
where   nr_sequencia = nr_seq_lote_imp_p;

commit;

-- Clean temporary tables
delete from w_pls_mat_unimed_trib;

commit;

delete from w_pls_mat_unimed_bras;

commit;

delete from w_pls_mat_unimed_simpro;

commit;

delete from w_pls_material_unimed;

commit;

select 	max(nr_versao_ptu),
	coalesce(max(ie_tipo_arquivo_imp), 'M1'),
	max(ie_tipo_arquivo)
into STRICT	nr_versao_ptu_w,
	ie_tipo_arquivo_imp_w,
	ie_tipo_arquivo_w
from	pls_lote_imp_mat_unimed
where	nr_sequencia = nr_seq_lote_imp_p;

if (ie_tipo_arquivo_imp_w = 'M1') then
	if (upper(nm_arquivo_p) like('%M2%')) or (upper(nm_arquivo_p) like('%M3%')) then
		ds_erro_w := wheb_mensagem_pck.get_texto(195168);
		RAISE EXCEPTION 'exception_w' USING ERRCODE = '50008';
	end if;
elsif (ie_tipo_arquivo_imp_w = 'M2') then
	if (upper(nm_arquivo_p) like('%M1%')) or (upper(nm_arquivo_p) like('%M3%')) then
		ds_erro_w := wheb_mensagem_pck.get_texto(291058);
		RAISE EXCEPTION 'exception_w' USING ERRCODE = '50008';
	end if;
elsif (ie_tipo_arquivo_imp_w = 'M3') then
	if (upper(nm_arquivo_p) like('%M1%')) or (upper(nm_arquivo_p) like('%M2%')) then
		ds_erro_w := wheb_mensagem_pck.get_texto(291059);
		RAISE EXCEPTION 'exception_w' USING ERRCODE = '50008';
	end if;
end if;

if (ie_tipo_arquivo_w = 'TXT') then
	-- Get path
	SELECT * FROM obter_evento_utl_file(2, null, ds_local_w, ds_erro_w) INTO STRICT ds_local_w, ds_erro_w;

	if (substr(ds_local_w,length(ds_local_w) -1,length(ds_local_w) ) <> '\\' ) then
		ds_local_w := ds_local_w || '\\';
	end if;
	
	ds_local_w := ds_local_w || nm_arquivo_p;

elsif (ie_tipo_arquivo_w = 'XML') then
		ds_local_w := nm_arquivo_p;
end if;

-- Update the import lot
update 	pls_lote_imp_mat_unimed
set	dt_inicio_importacao = clock_timestamp(),
	nm_arquivo = ds_local_w
where	nr_sequencia = nr_seq_lote_imp_p;

if (ie_tipo_arquivo_w = 'TXT') then
	-- File TXT
	CALL pls_utl_file_pck.nova_leitura(2, nm_arquivo_p);

	while(ie_leitura_w) loop
		-- Return file line
		SELECT * FROM pls_utl_file_pck.ler(ds_linha_w, ie_leitura_w) INTO STRICT ds_linha_w, ie_leitura_w;
		ds_erro_w := null;

		ie_tipo_w := substr(ds_linha_w, 9, 3);
		qt_registro_w := qt_registro_w + 1;

		if (ie_tipo_w = '901') then
			ie_versao_w := (substr(ds_linha_w, 25, 2))::numeric;
		end if;

		if (ie_versao_w = 9) then -- PTU 11.2
			SELECT * FROM pls_gerar_import_mat_v112(  	ds_linha_w, ie_tipo_w, nr_seq_lote_imp_p, nm_arquivo_p, nm_usuario_p, nr_seq_mat_unimed_w, nm_material_w, ie_tipo_pai_w, qt_linha_filha_w, ds_erro_w ) INTO STRICT nr_seq_mat_unimed_w, nm_material_w, ie_tipo_pai_w, qt_linha_filha_w, ds_erro_w;				
		elsif (ie_versao_w = 10) then -- PTU 12.0
			SELECT * FROM pls_gerar_import_mat_v120(  	ds_linha_w, ie_tipo_w, nm_usuario_p, nr_seq_mat_unimed_w, nm_material_w, ie_tipo_pai_w, qt_linha_filha_w, ds_erro_w ) INTO STRICT nr_seq_mat_unimed_w, nm_material_w, ie_tipo_pai_w, qt_linha_filha_w, ds_erro_w;				
		end if;

		if (qt_registro_w = 10000) then
			commit;
			qt_registro_w := 0;
		end if;

		if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
			RAISE EXCEPTION 'exception_w' USING ERRCODE = '50008';
		end if;
	end loop;
elsif (ie_tipo_arquivo_w = 'XML') then
	-- File XML
	select 	ds_arquivo
	into STRICT	ds_arquvio_w
	from	w_pls_mat_unimed_xml;

	ds_erro_w := PLS_INTERPRET_A900_CONTENT_XML.INTERPR_AND_PREPARE_TO_INSERT(ds_arquvio_w, nm_usuario_p, ds_erro_w);

	if  	(ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
		RAISE EXCEPTION 'exception_w' USING ERRCODE = '50008';
	end if;

end if;

CALL pls_utl_file_pck.fechar_arquivo();

-- Update the import lot
update 	pls_lote_imp_mat_unimed
set	dt_fim_importacao = clock_timestamp(),
	nm_usuario_imp = nm_usuario_p,
	ie_status = '3',
	ds_log  = NULL
where	nr_sequencia = nr_seq_lote_imp_p;

commit;

dbms_stats.gather_table_stats(USER,'W_PLS_MAT_UNIMED_SIMPRO');

exception
when SQLSTATE '50008' then	
	rollback;

	CALL pls_utl_file_pck.fechar_arquivo();

	update 	pls_lote_imp_mat_unimed
	set 	dt_fim_importacao  = NULL,
		dt_inicio_importacao  = NULL,
		nm_usuario_imp = nm_usuario_p,
		ie_status = '4',
		ds_log = ds_erro_w
	where 	nr_sequencia = nr_seq_lote_imp_p;

	commit;

when others then
	ds_erro_w := 'Erro: pls_gerar_import_mat_a900_new: ' || sqlerrm;
	rollback;

	CALL pls_utl_file_pck.fechar_arquivo();

	update 	pls_lote_imp_mat_unimed
	set 	dt_fim_importacao  = NULL,
		dt_inicio_importacao  = NULL,
		nm_usuario_imp = nm_usuario_p,
		ie_status = '4',
		ds_log = ds_erro_w
	where 	nr_sequencia = nr_seq_lote_imp_p;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_import_mat_a900_new ( nr_seq_lote_imp_p bigint, nm_arquivo_p text, nm_usuario_p text ) FROM PUBLIC;

