CREATE OR REPLACE PROCEDURE pls_gerar_inform_conta_interca ( nr_seq_analise_p bigint, nr_seq_consulta_benef_p bigint, nr_seq_param1_p bigint, nr_seq_param2_p bigint, nr_seq_param3_p bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Vincular a tabela PLS_ANALISE_INF_CONTA_INT  com a análise das contas e com a consulta de dados do beneficiário do PTU.

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_inf_conta_int_w	pls_analise_inf_conta_int.nr_sequencia%type;	--Number
nr_seq_segurado_w	pls_analise_conta.nr_seq_segurado%type;		--Number
BEGIN

begin
	select	nr_seq_segurado
	into STRICT	nr_seq_segurado_w
	from	pls_analise_conta
	where	nr_sequencia	= nr_seq_analise_p;
exception
when others then
	nr_seq_segurado_w	:= null;
end;

if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_inf_conta_int_w
	from	pls_analise_inf_conta_int
	where	nr_seq_analise	= nr_seq_analise_p;

	--Só será criado um novo registro na tabela caso não exista registros de consulta de elegibilidade para análise em questão,
	if (coalesce(nr_seq_inf_conta_int_w::text, '') = '') then
		select	nextval('pls_analise_inf_conta_int_seq')
		into STRICT	nr_seq_inf_conta_int_w
		;

		insert	into pls_analise_inf_conta_int(nr_sequencia, dt_atualizacao, nm_usuario,
			 dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_analise,
			 nr_seq_segurado, ie_situacao_origem, ie_tipo_validacao)
		values (nr_seq_inf_conta_int_w, clock_timestamp(), nm_usuario_p,
			 clock_timestamp(), nm_usuario_p, nr_seq_analise_p,
			 nr_seq_segurado_w, null, 1);
	else
		update	pls_analise_inf_conta_int
		set	dt_atualizacao		= clock_timestamp(),
			dt_atualizacao_nrec	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			nm_usuario_nrec		= nm_usuario_p,
			nr_seq_segurado		= nr_seq_segurado_w,
			ie_tipo_validacao	= 1
		where	nr_sequencia		= nr_seq_inf_conta_int_w;
	end if;

	update	ptu_consulta_beneficiario
	set	nr_seq_inf_conta_interc	= nr_seq_inf_conta_int_w
	where	nr_sequencia		= nr_seq_consulta_benef_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
