CREATE OR REPLACE PROCEDURE pls_gerar_itens_adic_xml_fat ( nr_seq_regra_p bigint, ie_valor_filme_p text, nr_seq_fatura_conta_p bigint, nr_seq_conta_proc_p bigint, ie_commit_p text, nm_usuario_p text ) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar os itens adicionais para o XML de faturamento
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_regra_w			bigint;


BEGIN
if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	insert into pls_fat_proc_adic(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_fatura_conta,
		cd_proc_item,
		ds_proc_item,
		cd_tab_item,
		nr_seq_conta_proc,
		ie_valor_filme)
	SELECT	nextval('pls_fat_proc_adic_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_fatura_conta_p,
		cd_proc_item,
		ds_proc_item,
		cd_tab_item,
		nr_seq_conta_proc_p,
		ie_valor_filme_p
	from	pls_fat_regra_proc_item
	where	nr_seq_regra	= nr_seq_regra_w;
end if;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
