CREATE OR REPLACE PROCEDURE pls_gerar_lanc_bonif_indicacao ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, dt_referencia_p pls_mensalidade_segurado.dt_mesano_referencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


dt_referencia_w			pls_segurado_mensalidade.dt_referencia%type;
qt_parcelas_pagas_w		integer;

C01 CURSOR(	nr_seq_segurado_pc	pls_segurado.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia nr_seq_indicacao,
		a.nr_seq_segurado,
		a.nr_seq_bonificacao,
		coalesce(a.qt_meses_bonificacao,0) qt_meses_bonificacao,
		a.qt_parcela_paga
	from	pls_indicacao_venda		a
	where	a.nr_seq_segurado_indicacao	= nr_seq_segurado_pc
	and	(a.qt_parcela_paga IS NOT NULL AND a.qt_parcela_paga::text <> '')
	and	not exists (	SELECT	1
					from	pls_segurado_mensalidade t
					where	t.nr_seq_indicacao_venda	= a.nr_sequencia
					and	t.nr_seq_segurado		= nr_seq_segurado_pc)
	group by	a.nr_seq_bonificacao,
			a.nr_seq_segurado,
			a.nr_sequencia,
			a.qt_meses_bonificacao,
			a.qt_parcela_paga;
BEGIN

for r_c01_w in C01(nr_seq_segurado_p) loop
	begin
	select	count(1)
	into STRICT	qt_parcelas_pagas_w
	from	titulo_receber		a,
		pls_mensalidade		b,
		pls_mensalidade_segurado c
	where	a.nr_seq_mensalidade	= b.nr_sequencia
	and	c.nr_seq_mensalidade	= b.nr_sequencia
	and	c.nr_seq_segurado	= r_c01_w.nr_seq_segurado
	and	a.ie_situacao		= '2';

	if (qt_parcelas_pagas_w >= r_c01_w.qt_parcela_paga) then

		for i in 0..r_c01_w.qt_meses_bonificacao-1 loop
			begin
			dt_referencia_w	:= add_months(dt_referencia_p, i);

			insert	into	pls_segurado_mensalidade(	nr_sequencia, nr_seq_segurado, cd_estabelecimento,
					dt_referencia, ie_tipo_item, dt_atualizacao,
					nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					nr_seq_bonificacao, ie_situacao, ie_tipo_lanc,
					nr_seq_indicacao_venda)
				values (	nextval('pls_segurado_mensalidade_seq'), nr_seq_segurado_p, cd_estabelecimento_p,
					dt_referencia_w, '14', clock_timestamp(),
					nm_usuario_p, clock_timestamp(), nm_usuario_p,
					r_c01_w.nr_seq_bonificacao, 'A', 'B',
					r_c01_w.nr_seq_indicacao);
			end;
		end loop;

	end if;
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
