CREATE OR REPLACE PROCEDURE pls_gerar_lanc_comissoes ( nr_titulo_p bigint, nr_seq_repasse_p bigint, nr_seq_comissao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

			 
/* nr_seq_vendedor_p: 
	este parâmetro só precisa ser passado quando o repasse do tipo normal estiver sendo recalculado. 
*/
 
 
vl_lancamento_w			double precision := 0;
vl_juros_multa_w		double precision := 0;
			

BEGIN 
 
vl_juros_multa_w := pls_obter_vl_juros_mult_titulo(nr_titulo_p);
 
if (vl_juros_multa_w > 0) then 
 
	insert into pls_repasse_lanc(	nr_sequencia, nr_seq_repasse, nr_seq_comissao, 
				vl_lancamento, vl_lanc_aplicado, ie_tipo_lancamento, 
				dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, 
				nr_seq_regra, nr_seq_lancamento_vend, nr_titulo ) 
		values (	nextval('pls_repasse_lanc_seq'), nr_seq_repasse_p, nr_seq_comissao_p, 
				vl_juros_multa_w, vl_juros_multa_w, '5', -- ie_tipo_lancamento = 5 = Acréscimo referente aos juros e multas do título a receber				 
				clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 
				null, null, nr_titulo_p);
				 
end if;
				 
--commit; 
 
end;
