CREATE OR REPLACE PROCEDURE pls_gerar_lanc_indicacao_pp ( nr_seq_segurado_p bigint, dt_mesano_referencia_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
nr_seq_bonificacao_w		bigint;
dt_referencia_w			timestamp;
nr_seq_indicacao_w		bigint;
qt_meses_bonificacao_w		bigint;
qt_interv_meses_w		bigint;
qt_parcela_paga_w		bigint;
qt_parcelas_pagas_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_sequencia,
		a.nr_seq_bonificacao,
		coalesce(a.qt_meses_bonificacao,0),
		a.qt_parcela_paga
	from	pls_indicacao_venda		a,
		pls_segurado			b
	where	a.nr_seq_segurado		= b.nr_sequencia
	and	a.nr_seq_segurado_indicacao	= nr_seq_segurado_p
	and	(a.qt_parcela_paga IS NOT NULL AND a.qt_parcela_paga::text <> '')
	and	not exists (	SELECT	1
					from	pls_segurado_mensalidade t
					where	t.nr_seq_indicacao_venda	= a.nr_sequencia
					and	t.nr_seq_segurado		= nr_seq_segurado_p)
	group by	a.nr_seq_bonificacao,
			b.nr_sequencia,
			a.nr_sequencia,
			a.qt_meses_bonificacao,
			a.qt_parcela_paga;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_indicacao_w,
	nr_seq_segurado_w,
	nr_seq_bonificacao_w,
	qt_meses_bonificacao_w,
	qt_parcela_paga_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(1)
	into STRICT	qt_parcelas_pagas_w
	from	titulo_receber		a,
		pls_mensalidade		b,
		pls_mensalidade_segurado c,
		pls_segurado		d
	where	a.nr_seq_mensalidade	= b.nr_sequencia
	and	c.nr_seq_mensalidade	= b.nr_sequencia
	and	c.nr_seq_segurado	= d.nr_sequencia
	and	d.nr_sequencia		= nr_seq_segurado_w
	and	a.ie_situacao		= '2';

	if (qt_parcela_paga_w = qt_parcelas_pagas_w) then
		qt_interv_meses_w	:= 0;
		while(qt_meses_bonificacao_w > 0) or (qt_interv_meses_w = 0) loop
			dt_referencia_w	:= add_months(dt_mesano_referencia_p,qt_interv_meses_w);

			insert	into	pls_segurado_mensalidade(	nr_sequencia, nr_seq_segurado, cd_estabelecimento,
					dt_referencia, ie_tipo_item, dt_atualizacao,
					nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					nr_seq_bonificacao, ie_situacao, ie_tipo_lanc,
					nr_seq_indicacao_venda)
				values (	nextval('pls_segurado_mensalidade_seq'), nr_seq_segurado_p, cd_estabelecimento_p,
					dt_referencia_w, '14', clock_timestamp(),
					nm_usuario_p, clock_timestamp(), nm_usuario_p,
					nr_seq_bonificacao_w, 'A', 'B',
					nr_seq_indicacao_w);
			qt_meses_bonificacao_w	:= qt_meses_bonificacao_w - 1;
			qt_interv_meses_w	:= qt_interv_meses_w + 1;
		end loop;
	end if;
	end;
end loop;
close C01;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
