CREATE OR REPLACE PROCEDURE pls_gerar_local_atend_maq ( ie_acao_p text, nr_seq_maquina_p bigint, nr_seq_local_atend_p bigint, id_maquina_p text, ds_observacao_p text, ds_endereco_mac_p text, nm_usuario_p text, ie_situacao_p text) AS $body$
BEGIN


/*ie_acao_p
I - Insert
U - Update
*/
if (ie_acao_p = 'I') then

	insert into local_atend_med_maquina(
			nr_sequencia, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_local_atend,
			id_maquina, ds_observacao, ie_situacao,
			ds_endereco_mac)
		values (	nextval('local_atend_med_maquina_seq'), clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p, nr_seq_local_atend_p,
			id_maquina_p, ds_observacao_p, ie_situacao_p,
			ds_endereco_mac_p);

elsif (ie_acao_p = 'U' and (nr_seq_maquina_p IS NOT NULL AND nr_seq_maquina_p::text <> '')) then

	update	local_atend_med_maquina
	set	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		id_maquina = id_maquina_p,
		ds_observacao = ds_observacao_p,
		ie_situacao = ie_situacao_p,
		ds_endereco_mac = ds_endereco_mac_p
	where	nr_sequencia = nr_seq_maquina_p;

