CREATE OR REPLACE PROCEDURE pls_gerar_log_erro_rec_glosa ( nr_seq_protocolo_p bigint, nm_usuario_p text, nr_seq_mensagem_p bigint, ds_erro_p text) AS $body$
DECLARE


qt_mensagem_bloqueio_w		bigint;
qt_contas_w 			bigint;
nr_seq_mensagem_import_w	bigint;


BEGIN

if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	/* Salvar a mensagem de erro gerada na importação de XML */

	insert into pls_mensagem_imp_recurso(	nr_sequencia, dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
		nr_seq_mensagem,nr_seq_protocolo, ds_erro)
	values (nextval('pls_mensagem_imp_recurso_seq'), clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
		nr_seq_mensagem_p,nr_seq_protocolo_p, substr(ds_erro_p,0,2000));


	if (ds_erro_p IS NOT NULL AND ds_erro_p::text <> '') then
		/* Atualizar estágio do protocolo para Rejeitado devido ao erro gerado */

		update	pls_rec_glosa_protocolo
		set	ie_situacao		= 'RE',
			ie_status		= '1'
		where	nr_sequencia		= nr_seq_protocolo_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
