SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_lotes_reenvio_trans (ds_lista_p text, ie_tipo_reenvio_p text, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_lista_w			varchar(4000);
ie_tipo_reenvio_w		varchar(4000);
nr_seq_lote_novo_w		bigint;
nr_sequencia_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_requisicao a
	where	ds_lista_w like '% ' || a.nr_sequencia || ' %'
	and	(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '')
	and	ie_tipo_reenvio_w = 'R'
	
union all

	SELECT	a.nr_sequencia
	from	pls_guia_plano a
	where	ds_lista_w like '% ' || a.nr_sequencia || ' %'
	and	(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '')
	and	ie_tipo_reenvio_w = 'G';


BEGIN
if (ds_lista_p IS NOT NULL AND ds_lista_p::text <> '') and (ie_tipo_reenvio_p IS NOT NULL AND ie_tipo_reenvio_p::text <> '') then
	ds_lista_w 		:= ds_lista_p;
	ie_tipo_reenvio_w 	:= ie_tipo_reenvio_p;
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_tipo_reenvio_p = 'R') then
			nr_seq_lote_novo_w := pls_gerar_lote_reenvio_trans(null, nr_sequencia_w, nr_seq_lote_p, nm_usuario_p, nr_seq_lote_novo_w);
		elsif (ie_tipo_reenvio_p = 'G') then
			nr_seq_lote_novo_w := pls_gerar_lote_reenvio_trans(nr_sequencia_w, null, nr_seq_lote_p, nm_usuario_p, nr_seq_lote_novo_w);
		end if;
		end;
	end loop;
	close C01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
