CREATE OR REPLACE PROCEDURE pls_gerar_lote_analise_rec ( nr_seq_rec_glosa_prot_p pls_rec_glosa_protocolo.nr_sequencia%type, nr_seq_lote_protocolo_p pls_lote_protocolo_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

 
ie_status_w			pls_rec_glosa_protocolo.ie_status%type;
nr_seq_prestador_w		pls_rec_glosa_protocolo.nr_seq_prestador%type;
nr_seq_lote_protocolo_w		pls_lote_protocolo_conta.nr_sequencia%type := nr_seq_lote_protocolo_p;


BEGIN 
select	max(ie_status), 
	max(nr_seq_prestador) 
into STRICT	ie_status_w, 
	nr_seq_prestador_w 
from	pls_rec_glosa_protocolo 
where	nr_sequencia	= nr_seq_rec_glosa_prot_p;
 
-- Protocolo deve estar com status "Em an√°lise" 
if (ie_status_w = '2') then 
 
	if (coalesce(nr_seq_lote_protocolo_w::text, '') = '') then 
		insert	into pls_lote_protocolo_conta(nr_sequencia, 
			dt_atualizacao, 
			dt_atualizacao_nrec, 
			nm_usuario, 
			nm_usuario_nrec, 
			dt_lote, 
			nr_seq_prestador, 
			ie_tipo_lote, 
			cd_estabelecimento, 
			ie_origem_analise) 
		values (nextval('pls_lote_protocolo_conta_seq'), 
			clock_timestamp(), 
			clock_timestamp(), 
			nm_usuario_p, 
			nm_usuario_p, 
			clock_timestamp(), 
			nr_seq_prestador_w, 
			'P', 
			cd_estabelecimento_p, 
			4) returning nr_sequencia into nr_seq_lote_protocolo_w;
 
		update	pls_rec_glosa_protocolo 
		set	nr_seq_lote_conta	= nr_seq_lote_protocolo_w 
		where	nr_sequencia		= nr_seq_rec_glosa_prot_p;
	end if;
 
	CALL pls_gerar_analise_lote( nr_seq_lote_protocolo_w, 'N', 'N', cd_estabelecimento_p, nm_usuario_p, null, null, '');
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
