CREATE OR REPLACE PROCEDURE pls_gerar_lote_apres_aut ( dt_ref_inicial_p timestamp, dt_ref_final_p timestamp, ie_preco_p text, ie_referencia_data_p text, ie_situacao_cid_p text, ie_tipo_guia_p text, nr_seq_prestador_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_lote_p INOUT bigint) AS $body$
DECLARE


ie_tipo_guia_w			varchar(2);
nr_seq_prestador_w 		bigint	:= null;


BEGIN
if (nr_seq_prestador_p > 0) then
nr_seq_prestador_w	:= nr_seq_prestador_p;
end if;

select	CASE WHEN ie_tipo_guia_p='1' THEN '5' WHEN ie_tipo_guia_p='2' THEN '4' WHEN ie_tipo_guia_p='3' THEN '3' WHEN ie_tipo_guia_p='8' THEN '5' END
into STRICT	ie_tipo_guia_w
;

select	nextval('pls_lote_apres_automatica_seq')
into STRICT	nr_seq_lote_p
;

insert into pls_lote_apres_automatica(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	dt_ref_inicial,
	dt_ref_final,
	cd_estabelecimento,
	nr_seq_prestador,
	ie_preco,
	ie_referencia_data,
	ie_situacao_cid,
	ie_tipo_guia,
	dt_integracao)
values (nr_seq_lote_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	dt_ref_inicial_p,
	dt_ref_final_p,
	cd_estabelecimento_p,
	nr_seq_prestador_w,
	ie_preco_p,
	ie_referencia_data_p,
	ie_situacao_cid_p,
	ie_tipo_guia_w,
	clock_timestamp());
commit;

end;
$body$
