CREATE OR REPLACE PROCEDURE pls_gerar_lote_evento_pag ( nr_seq_lote_pagamento_p bigint, nm_usuario_p text, cd_estabelecimento_p text, nr_seq_lote_evento_p INOUT bigint) AS $body$
DECLARE

 
dt_inicio_comp_w		timestamp;
dt_fim_comp_w			timestamp;	
dt_mes_competencia_w		timestamp;
nr_seq_lote_evento_w		bigint;
qt_lote_evento_w		integer;
ds_erro_w			varchar(4000);
qt_vago_w			bigint;


BEGIN 
if (nr_seq_lote_pagamento_p IS NOT NULL AND nr_seq_lote_pagamento_p::text <> '') then 
	select	dt_inicio_comp, 
		dt_fim_comp, 
		dt_mes_competencia 
	into STRICT	dt_inicio_comp_w, 
		dt_fim_comp_w, 
		dt_mes_competencia_w 
	from	pls_lote_pagamento 
	where	nr_sequencia = nr_seq_lote_pagamento_p;
	 
	select	count(1) 
	into STRICT	qt_lote_evento_w 
	from	pls_lote_evento	a 
	where	nr_seq_lote_pagamento = nr_seq_lote_pagamento_p  LIMIT 1;
	 
	if (qt_lote_evento_w = 0) then 
		select	nextval('pls_lote_evento_seq') 
		into STRICT	nr_seq_lote_evento_w 
		;
		 
		insert into pls_lote_evento(nr_sequencia, 
			nm_usuario, 
			dt_atualizacao, 
			nm_usuario_nrec, 
			dt_atualizacao_nrec, 
			ie_origem, 
			dt_inicio_comp, 
			dt_fim_comp, 
			nr_seq_lote_pagamento, 
			dt_liberacao, 
			dt_competencia, 
			cd_estabelecimento) 
		values (nr_seq_lote_evento_w, 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			'A', 
			dt_inicio_comp_w, 
			dt_fim_comp_w, 
			nr_seq_lote_pagamento_p, 
			clock_timestamp(), 
			dt_mes_competencia_w, 
			cd_estabelecimento_p);
		 
		CALL gerar_lancamento_programado(nr_seq_lote_evento_w,cd_estabelecimento_p,nm_usuario_p,'N');
		CALL pls_atualizar_codificacao_pck.pls_atualizar_codificacao(trunc(dt_mes_competencia_w,'month'));
		 
		begin 
		qt_vago_w := ctb_pls_atualizar_prod_eve_in(nr_seq_lote_evento_w, null, null, nm_usuario_p, cd_estabelecimento_p, qt_vago_w);
		exception 
		when others then 
			ds_erro_w	:= '';
		end;
	end if;
	 
	nr_seq_lote_evento_p	:= nr_seq_lote_evento_w;
end if;
 
/*NÃ£o pode ter commit*/
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
