CREATE OR REPLACE PROCEDURE pls_gerar_mensagem_cobr_flash (ds_conteudo_p text, cd_flash_p text, ie_tipo_coluna_p INOUT text, nr_seq_cobranca_p bigint, nr_seq_linha_p bigint, ie_opcao_p text, ie_elimina_caract_esp_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina utilizada para a geração das mensagens FLASH - muito cuidado ao alterar
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Abrir os arquvios de remessa - na nossa base           \\192.168.0.230\UTLFILE

Caso não entender a logica, falar com wcbernardino - OPS

Rotinas que utilizam:
	CRUSAM_GERAR_COBRANCA_ITAU_400
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_sequencia_w		bigint;
nr_seq_coluna_w		bigint;
nr_seq_linha_w		bigint;
ds_conteudo_w		varchar(4000);
ds_conteudo_compl_w	varchar(4000);
ds_conteudo_msg_w	varchar(4000);
nr_seq_mensagem_w	bigint;


BEGIN
if (ie_opcao_p = '1') then
	select	coalesce(max(nr_seq_coluna),0) + 1
	into STRICT	nr_seq_coluna_w
	from	w_pls_mensagem_tit
	where	nm_usuario	= nm_usuario_p
	and	nr_seq_cobranca	= nr_seq_cobranca_p;

	ds_conteudo_msg_w := ds_conteudo_p;

	if (coalesce(ie_elimina_caract_esp_p,'N') = 'S') then
		select	elimina_caractere_especial(ds_conteudo_p)
		into STRICT	ds_conteudo_msg_w
		;
	end if;

	if (ie_tipo_coluna_p = '1') then
		ds_conteudo_w := 	cd_flash_p || lpad(nr_seq_coluna_w,2,'0') || substr(rpad(coalesce(ds_conteudo_msg_w,' '),128,' '),1,128);

		select	nextval('w_pls_mensagem_tit_seq')
		into STRICT	nr_sequencia_w
		;

		insert into w_pls_mensagem_tit(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_conteudo,
			ie_tipo_coluna,
			nr_seq_coluna,
			nr_seq_cobranca,
			nr_seq_linha)
		values (nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_conteudo_w,
			ie_tipo_coluna_p,
			nr_seq_coluna_w,
			nr_seq_cobranca_p,
			nr_seq_linha_p);

	elsif (ie_tipo_coluna_p = '2') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_mensagem_w
		from	w_pls_mensagem_tit
		where	nm_usuario	= nm_usuario_p
		and	nr_seq_cobranca	= nr_seq_cobranca_p;

		select	ds_conteudo
		into STRICT	ds_conteudo_compl_w
		from	w_pls_mensagem_tit
		where	nr_sequencia = nr_seq_mensagem_w;

		ds_conteudo_w :=	ds_conteudo_compl_w || lpad(nr_seq_coluna_w,2,'0') || substr(rpad(coalesce(ds_conteudo_msg_w,' '),128,' '),1,128);

		update	w_pls_mensagem_tit
		set	ds_conteudo	= ds_conteudo_w,
			nr_seq_coluna	= nr_seq_coluna_w
		where	nr_sequencia	= nr_seq_mensagem_w;

	elsif (ie_tipo_coluna_p = '3') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_mensagem_w
		from	w_pls_mensagem_tit
		where	nm_usuario	= nm_usuario_p
		and	nr_seq_cobranca	= nr_seq_cobranca_p;

		select	ds_conteudo
		into STRICT	ds_conteudo_compl_w
		from	w_pls_mensagem_tit
		where	nr_sequencia = nr_seq_mensagem_w;

		select	coalesce(max(nr_seq_linha),0) + 1
		into STRICT	nr_seq_linha_w
		from	w_pls_mensagem_tit
		where	nm_usuario	= nm_usuario_p
		and	nr_seq_cobranca	= nr_seq_cobranca_p;

		ds_conteudo_w := 	ds_conteudo_compl_w || lpad(nr_seq_coluna_w,2,'0') || substr(rpad(coalesce(ds_conteudo_msg_w,' '),128,' '),1,128) ||
					lpad(nr_seq_linha_w,6,0);

		update	w_pls_mensagem_tit
		set	ds_conteudo	= ds_conteudo_w,
			nr_seq_linha	= nr_seq_linha_w,
			nr_seq_coluna	= nr_seq_coluna_w
		where	nr_sequencia	= nr_seq_mensagem_w;
	end if;

	case	ie_tipo_coluna_p
		when '1' then ie_tipo_coluna_p := '2';
		when '2' then ie_tipo_coluna_p := '3';
		when '3' then ie_tipo_coluna_p := '1';
		else ie_tipo_coluna_p := '1';
	end case;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_mensagem_cobr_flash (ds_conteudo_p text, cd_flash_p text, ie_tipo_coluna_p INOUT text, nr_seq_cobranca_p bigint, nr_seq_linha_p bigint, ie_opcao_p text, ie_elimina_caract_esp_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

