CREATE OR REPLACE PROCEDURE pls_gerar_mensagem_email_mens ( nr_seq_regra_p pls_mensagem_email.nr_sequencia%type, nr_seq_mensagem_p INOUT w_pls_envio_email.nr_sequencia%type) AS $body$
DECLARE


nr_seq_envio_email_w		w_pls_envio_email.nr_sequencia%type;
nm_usuario_w			usuario.nm_usuario%type;


BEGIN

nm_usuario_w	:= wheb_usuario_pck.get_nm_usuario;

delete from w_pls_envio_email
where	nm_usuario = nm_usuario_w
and	dt_atualizacao < (clock_timestamp() - interval '2 days');

select	nextval('w_pls_envio_email_seq')
into STRICT	nr_seq_envio_email_w
;

insert into w_pls_envio_email(	nr_sequencia, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, ds_mensagem,
		ds_assunto)
	(SELECT	nr_seq_envio_email_w, clock_timestamp(),	nm_usuario_w,
		clock_timestamp(), nm_usuario_w, ds_mensagem,
		ds_assunto
	from	pls_mensagem_email
	where	nr_sequencia	= nr_seq_regra_p);

commit;

nr_seq_mensagem_p := nr_seq_envio_email_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
