CREATE OR REPLACE PROCEDURE pls_gerar_mens_log_erro ( nr_seq_lote_p bigint, nr_seq_pagador_p bigint, nr_seq_segurado_p bigint, nr_seq_mensalidade_p bigint, ds_mensagem_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_mens_log_w	bigint;
ie_gerar_log_w		varchar(1);
dt_rescisao_w		timestamp;
dt_referencia_w		timestamp;
ds_mensagem_w		varchar(4000);
ds_pagador_w		varchar(4000);
ds_segurado_w		varchar(4000);
ds_lote_w			varchar(4000);


BEGIN
ie_gerar_log_w	:= 'S';
if (coalesce(nr_seq_segurado_p,0) <> 0) then
	select	max(dt_rescisao)
	into STRICT	dt_rescisao_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_p;

	if (dt_rescisao_w IS NOT NULL AND dt_rescisao_w::text <> '') then
		select	max(dt_referencia)
		into STRICT	dt_referencia_w
		from	pls_mensalidade
		where	nr_sequencia	= nr_seq_mensalidade_p;

		if (dt_rescisao_w < dt_referencia_w) then
			ie_gerar_log_w	:= 'N';
		end if;
	end if;
end if;

if (ie_gerar_log_w = 'S') then
	select	CASE WHEN coalesce(nr_seq_Pagador_P::text, '') = '' THEN  ''  ELSE ' Pagador - '|| nr_seq_pagador_p END ,
			CASE WHEN coalesce(nr_seq_segurado_p::text, '') = '' THEN  ''  ELSE ' BeneficiÃ¡rio - '|| nr_seq_segurado_p END ,
			CASE WHEN coalesce(nr_seq_lote_p::text, '') = '' THEN  ''  ELSE ' Lote Mensalidade - '||nr_seq_lote_p END
	into STRICT	ds_pagador_w,
			ds_segurado_w,
			ds_lote_w
	;

	ds_mensagem_w := ds_mensagem_p || ds_pagador_w || ds_segurado_w || ds_lote_w;

	if (pls_mensalidade_util_pck.get_parametro_mens = 'S') then
		CALL pls_utl_file_pck.escrever(ds_mensagem_w);
	end if;

	select	nextval('pls_mensalidade_log_seq')
	into STRICT	nr_seq_mens_log_w
	;

	insert into	pls_mensalidade_log(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
			cd_estabelecimento,nr_seq_lote,nr_seq_pagador,ds_mensagem,nr_seq_segurado,
			nr_seq_mensalidade)
	values (	nr_seq_mens_log_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
			cd_estabelecimento_p,nr_seq_lote_p,nr_seq_pagador_p,ds_mensagem_p,nr_seq_segurado_p,
			nr_seq_mensalidade_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
