CREATE OR REPLACE PROCEDURE pls_gerar_mens_manutenc_inter ( nr_seq_mensalidade_seg_p bigint, nr_seq_intercambio_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_mensalidade_w	pls_mensalidade_seg_item.nr_sequencia%type;
vl_manutencao_inter_w		double precision;
dt_mesano_referencia_w		timestamp;


BEGIN

dt_mesano_referencia_w	:= pls_store_data_mens_pck.get_dt_mesano_referencia;

select	sum(vl_manutencao)
into STRICT	vl_manutencao_inter_w
from	pls_regra_vl_intercambio
where	nr_seq_intercambio	= nr_seq_intercambio_p
and	dt_mesano_referencia_w between coalesce(dt_inicio_vigencia,dt_mesano_referencia_w) and coalesce(dt_fim_vigencia,dt_mesano_referencia_w);

if (coalesce(vl_manutencao_inter_w,0) > 0) then

	nr_seq_item_mensalidade_w := null;

	nr_seq_item_mensalidade_w := pls_insert_mens_seg_item('34', nm_usuario_p, null, null, null, null, null, null, null, 'N', null, null, null, null, null, null, null, nr_seq_mensalidade_seg_p, null, null, null, null, null, null, null, null, null, null, null, null, null, vl_manutencao_inter_w, nr_seq_item_mensalidade_w);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
