CREATE OR REPLACE PROCEDURE pls_gerar_mens_via_adicional ( nr_seq_mensalidade_seg_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
nr_seq_segurado_cart_w		bigint;
vl_via_adicional_w		double precision;
nr_seq_item_mensalidade_w	pls_mensalidade_seg_item.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		vl_via_adicional
	from	pls_segurado_carteira
	where	nr_seq_segurado		= nr_seq_segurado_w
	/*and	nr_via_solicitacao	> 1 aaschlote 10/05/2012 - OS 441961*/

	and	coalesce(nr_seq_mensalidade_seg::text, '') = '';


BEGIN

nr_seq_segurado_w	:= pls_store_data_mens_pck.get_nr_seq_segurado;

open C01;
loop
fetch C01 into
	nr_seq_segurado_cart_w,
	vl_via_adicional_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(vl_via_adicional_w,0) > 0) then

		nr_seq_item_mensalidade_w := null;

		nr_seq_item_mensalidade_w := pls_insert_mens_seg_item('11', nm_usuario_p, null, null, null, null, null, null, null, 'N', null, null, null, nr_seq_segurado_cart_w, null, null, null, nr_seq_mensalidade_seg_p, null, null, null, null, null, null, null, null, null, null, null, null, null, vl_via_adicional_w, nr_seq_item_mensalidade_w);

		if (nr_seq_item_mensalidade_w IS NOT NULL AND nr_seq_item_mensalidade_w::text <> '') then
			update	pls_segurado_carteira
			set	nr_seq_mensalidade_seg	= nr_seq_mensalidade_seg_p
			where	nr_sequencia		= nr_seq_segurado_cart_w;
		end if;
	end if;
	end;
end loop;
close C01;

/* NÃ£o pode dar commit */

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
