CREATE OR REPLACE PROCEDURE pls_gerar_movimento_sca ( nr_seq_lote_p bigint, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_plano_w			bigint;
nr_seq_prestador_w		bigint;
ie_tipo_movimento_w		varchar(2);
dt_inicial_w			timestamp;
dt_final_w			timestamp;
nr_seq_segurado_w		bigint;
dt_inicio_vigencia_w		timestamp;
dt_fim_vigencia_w		timestamp;
ie_gerou_lote_w			varchar(1)	:= 'N';

C01 CURSOR FOR
	SELECT	a.nr_seq_segurado
	from	pls_segurado_sca_v a,
		pls_plano b
	where	a.nr_seq_sca	= b.nr_sequencia
	and	coalesce(nr_seq_plano_w,a.nr_seq_sca)	= a.nr_seq_sca
	and	exists (	SELECT	1
				from	pls_plano_fornecedor x
				where	b.nr_sequencia	= x.nr_seq_plano
				and	coalesce(nr_seq_prestador_w,x.nr_seq_prestador)	= x.nr_seq_prestador)
	and	((ie_tipo_movimento_w = 'T') or (ie_tipo_movimento_w = 'I' and a.dt_inicio_vigencia between trunc(coalesce(dt_inicial_w,a.dt_inicio_vigencia),'dd') and fim_dia(coalesce(dt_final_w,a.dt_inicio_vigencia))) or (ie_tipo_movimento_w = 'E' and a.dt_fim_vigencia between trunc(coalesce(dt_inicial_w,a.dt_inicio_vigencia),'dd') and fim_dia(coalesce(dt_final_w,a.dt_inicio_vigencia))));


BEGIN

if (ie_opcao_p	= 'C') then
	select	nr_seq_plano,
		nr_seq_prestador,
		ie_tipo_movimento,
		dt_inicial,
		dt_final
	into STRICT	nr_seq_plano_w,
		nr_seq_prestador_w,
		ie_tipo_movimento_w,
		dt_inicial_w,
		dt_final_w
	from	pls_lote_movimento_sca
	where	nr_sequencia	= nr_seq_lote_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_segurado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_gerou_lote_w	:= 'S';

		insert into pls_movimento_sca(	nr_sequencia, nr_seq_lote, cd_estabelecimento, dt_atualizacao,
						nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_segurado)
					values (	nextval('pls_movimento_sca_seq'), nr_seq_lote_p, cd_estabelecimento_p, clock_timestamp(),
						nm_usuario_p, clock_timestamp(), nm_usuario_p, nr_seq_segurado_w);
		end;
	end loop;
	close C01;

	if (ie_gerou_lote_w = 'S') then
		update	pls_lote_movimento_sca
		set	dt_geracao_lote	= clock_timestamp(),
			nm_usuario_geracao	= nm_usuario_p
		where	nr_sequencia	= nr_seq_lote_p;
	end if;
elsif (ie_opcao_p	= 'D') then
	delete	from pls_movimento_sca
	where	nr_seq_lote	= nr_seq_lote_p;

	update	pls_lote_movimento_sca
	set	dt_geracao_lote	 = NULL,
		nm_usuario_geracao	 = NULL
	where	nr_sequencia	= nr_seq_lote_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
