CREATE OR REPLACE PROCEDURE pls_gerar_movto_bco_baixa_resc ( nr_seq_tit_rec_cobr_p bigint, nr_titulo_p bigint, nr_seq_trans_inad_p bigint, ie_acao_p text, dt_baixa_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_observacao_w			varchar(255);
vl_total_recebido_w		double precision := 0;
vl_recebido_w			double precision;
nr_seq_conta_banco_w		bigint;
cd_empresa_w			bigint;
nr_seq_cobr_escrit_w		bigint;
nr_seq_proj_rec_w		bigint;
nr_seq_movto_w			bigint;
cd_tipo_recebimento_w		integer;
dt_recebimento_w		timestamp;


BEGIN

/* Buscar as informações da cobrança escritural/titulo_rec_cobr - Conta bancária, data do recebimento, valor recebido.
A transação para lançamento da movimentação bancária será a do PLS_PARAMETROS_CR */
select	coalesce(a.dt_liquidacao,dt_baixa_p),
	a.vl_liquidacao,
	b.ds_observacao_titulo,
	a.nr_seq_cobranca,
	b.nr_seq_proj_rec,
	d.nr_seq_conta_banco
into STRICT	dt_recebimento_w,
	vl_recebido_w,
	ds_observacao_w,
	nr_seq_cobr_escrit_w,
	nr_seq_proj_rec_w,
	nr_seq_conta_banco_w
from	cobranca_escritural		d,
	parametro_contas_receber	c,
	titulo_receber			b,
	titulo_receber_cobr		a
where	b.nr_titulo		= a.nr_titulo
and	d.nr_sequencia		= a.nr_seq_cobranca
and	b.cd_estabelecimento	= c.cd_estabelecimento
and	a.nr_titulo		= nr_titulo_p
and	a.nr_sequencia		= nr_seq_tit_rec_cobr_p;

if (nr_seq_conta_banco_w IS NOT NULL AND nr_seq_conta_banco_w::text <> '') then
	if (ie_acao_p = 'E') then
		vl_total_recebido_w	:= (vl_recebido_w * -1);
	else
		vl_total_recebido_w	:= vl_recebido_w;
	end if;

	select	nextval('movto_trans_financ_seq')
	into STRICT	nr_seq_movto_w
	;

	insert into movto_trans_financ(nr_sequencia,
		dt_transacao,
		nr_seq_trans_financ,
		vl_transacao,
		dt_atualizacao,
		nm_usuario,
		nr_seq_titulo_receber,
		dt_referencia_saldo,
		nr_seq_banco,
		nr_lote_contabil,
		ie_conciliacao,
		ds_historico,
		nr_seq_cobr_escrit,
		nr_seq_proj_rec)
	values (nr_seq_movto_w,
		dt_recebimento_w,
		nr_seq_trans_inad_p,
		vl_total_recebido_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_titulo_p,
		clock_timestamp(),
		nr_seq_conta_banco_w,
		0,
		'N',
		ds_observacao_w,
		nr_seq_cobr_escrit_w,
		nr_seq_proj_rec_w);

	CALL pls_encerrar_cobr_titulo_inad(nr_titulo_p, cd_estabelecimento_p, nm_usuario_p);
end if;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
