CREATE OR REPLACE PROCEDURE pls_gerar_neg_cr_tit_mens ( nr_seq_pagador_p text, nr_titulo_p text, nm_usuario_p text, cd_estabelecimento_p text, nr_seq_negociacao_p INOUT bigint ) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
vl_juros_w			double precision;
vl_multa_w			double precision;
vl_negociado_w			double precision;


BEGIN

if (nr_seq_pagador_p IS NOT NULL AND nr_seq_pagador_p::text <> '') and (coalesce(nr_seq_negociacao_p::text, '') = '') then

	select	a.cd_pessoa_fisica,
		a.cd_cgc
	into STRICT	cd_pessoa_fisica_w,
		cd_cgc_w
	from	pls_contrato_pagador a
	where	a.nr_sequencia	= nr_seq_pagador_p;

	select	nextval('negociacao_cr_seq')
	into STRICT	nr_seq_negociacao_p
	;

	insert into	negociacao_cr(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_estabelecimento,
			cd_pessoa_fisica,
			cd_cgc,
			dt_negociacao,
			vl_negociado,
			vl_desconto,
			vl_multa,
			vl_juros,
			ie_status)
	values (
			nr_seq_negociacao_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_estabelecimento_p,
			cd_pessoa_fisica_w,
			cd_cgc_w,
			clock_timestamp(),
			0,
			0,
			0,
			0,
			'AS');
end if;

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	vl_juros_w	:= obter_juros_multa_titulo(nr_titulo_p,clock_timestamp(),'R','J');
	vl_multa_w	:= obter_juros_multa_titulo(nr_titulo_p,clock_timestamp(),'R','M');

	select	coalesce(max(vl_saldo_titulo),0)
	into STRICT	vl_negociado_w
	from    titulo_receber
	where   nr_titulo	= nr_titulo_p
	and     ie_situacao in ('1','6');

	insert into titulo_rec_negociado(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_negociacao,
		nr_titulo,
		vl_juros,
		vl_multa,
		vl_negociado)
	values (nextval('titulo_rec_negociado_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_negociacao_p,
		nr_titulo_p,
		vl_juros_w,
		vl_multa_w,
		vl_negociado_w);

end if;

CALL atualizar_valores_neg_cr(nr_seq_negociacao_p,nm_usuario_p,'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
