SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_ocorrencias_analise ( nr_seq_analise_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
qt_idade_w			bigint;
nr_seq_regra_ocorrencia_w	bigint;
nr_seq_pessoa_proposta_w	bigint;
nr_seq_proposta_w		bigint;
qt_vidas_proposta_w		bigint;
ie_tipo_contratacao_w		varchar(10);
nr_contrato_w			bigint;
nr_seq_contrato_w		bigint;
qt_vidas_contrato_w		bigint;
qt_total_vidas_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_regra_analise_adesao 
	where	qt_idade_w between coalesce(qt_idade_inicial,qt_idade_w) and coalesce(qt_idade_final,qt_idade_w) 
	and	qt_total_vidas_w between coalesce(qt_minima_vidas,qt_total_vidas_w) and coalesce(qt_maxima_vidas,qt_total_vidas_w) 
	and	((ie_tipo_contratacao	= ie_tipo_contratacao_w and (ie_tipo_contratacao IS NOT NULL AND ie_tipo_contratacao::text <> '')) or (coalesce(ie_tipo_contratacao::text, '') = '')) 
	and	ie_situacao	= 'A';


BEGIN 
 
select	substr(obter_dados_pf(cd_pessoa_fisica, 'I'),1,40), 
	nr_seq_pessoa_proposta 
into STRICT	qt_idade_w, 
	nr_seq_pessoa_proposta_w 
from	pls_analise_adesao 
where	nr_sequencia = nr_seq_analise_p;
 
if (nr_seq_pessoa_proposta_w IS NOT NULL AND nr_seq_pessoa_proposta_w::text <> '') then 
	select	nr_seq_proposta 
	into STRICT	nr_seq_proposta_w 
	from	pls_proposta_beneficiario 
	where	nr_sequencia	= nr_seq_pessoa_proposta_w;
	 
	/*dados da proposta*/
 
	select	ie_tipo_contratacao, 
		nr_seq_contrato 
	into STRICT	ie_tipo_contratacao_w, 
		nr_contrato_w 
	from	pls_proposta_adesao 
	where	nr_sequencia	= nr_seq_proposta_w;
	 
	if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') then 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_contrato_w 
		from	pls_contrato 
		where	nr_contrato	= nr_contrato_w;
		 
		if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then 
			select	count(*) 
			into STRICT	qt_vidas_contrato_w 
			from	pls_segurado a, 
				pls_plano b 
			where	b.nr_sequencia		= a.nr_seq_plano 
			and	a.nr_seq_contrato	= nr_seq_contrato_w 
			and	((b.ie_tipo_contratacao	= ie_tipo_contratacao_w and (ie_tipo_contratacao_w IS NOT NULL AND ie_tipo_contratacao_w::text <> '')) 
			or (coalesce(ie_tipo_contratacao_w::text, '') = '')) 
			and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
			and	coalesce(a.dt_rescisao::text, '') = '';
		end if;
	end if;
	 
	select	count(*) 
	into STRICT	qt_vidas_proposta_w 
	from	pls_proposta_beneficiario 
	where	nr_seq_proposta	= nr_seq_proposta_w;
end if;
 
qt_total_vidas_w	:= coalesce(qt_vidas_proposta_w,0) + coalesce(qt_vidas_contrato_w,0);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_regra_ocorrencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (nr_seq_regra_ocorrencia_w IS NOT NULL AND nr_seq_regra_ocorrencia_w::text <> '') then 
		insert into pls_analise_ocorrencia(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec, 
				cd_estabelecimento,nr_seq_analise,nr_seq_regra_ocorrencia) 
		values (	nextval('pls_analise_ocorrencia_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p, 
				cd_estabelecimento_p,nr_seq_analise_p,nr_seq_regra_ocorrencia_w);
	end if;			
	 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_ocorrencias_analise ( nr_seq_analise_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

