qt_item_solic_w		:= 0;

/* Obter parÃ¢metros da conta mÃ©dica */
 
 
if (coalesce(nr_seq_proc_p,0) <> 0) then 
	ie_tipo_item_w	:= 'P';

	if (coalesce(nr_seq_conta_p,0) > 0) then 
		;
