			qt_eventos_pagto_w	:= null;
			dt_inicio_proc_w	:= null;
			dt_fim_proc_w		:= null;
		end;
		 
		if (coalesce(tx_intercambio_w::text, '') = '') then 
			tx_intercambio_w	:= 0;
		end if;	
		if (coalesce(tx_intercambio_imp_w::text, '') = '') then 
			tx_intercambio_imp_w	:= 0;
		end if;
		 
