		end if;
		 
		if (coalesce(vl_apresentado_w::text, '') = '') then 
			vl_apresentado_w	:= 0;
		end if;
		 
		/*OS - 283335 - Utilizado em ocorrÃªncias de SEM CLASSIFICAÃÃO SIP*/
 
