CREATE OR REPLACE PROCEDURE pls_gerar_ocor_aut_aces_web ( nr_seq_ocor_filtro_p bigint, nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_execucao_p bigint, nr_seq_param1_p bigint, nr_seq_param2_p bigint, nr_seq_param3_p bigint, nr_seq_param4_p bigint, nr_seq_param5_p bigint, nm_usuario_p text, ie_gerar_ocorrencia_p INOUT text, ie_tipo_ocorrencia_p INOUT text) AS $body$
DECLARE



/*
IE_TIPO_OCORRENCIA_W	= C - Gera a ocorrência para o cabeçalho
			= I - Gera ocorrência para os itens
*/
ie_gerar_ocorrencia_w		varchar(2)	:= 'N';
ie_regra_com_itens_w		varchar(1);
nr_seq_requisicao_w		bigint;
nr_seq_prestador_web_w		bigint;
nr_seq_usuario_web_filtro_w	bigint;
nr_seq_local_atend_w		bigint;
nr_seq_local_atend_web_w	bigint;
nr_seq_lote_exec_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_usuario_web,
		nr_seq_local_atend
	from	pls_ocor_aut_filtro_web
	where	nr_seq_ocor_aut_filtro	= nr_seq_ocor_filtro_p
	and	ie_situacao	= 'A';



BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	begin
		select	nr_seq_prestador_web
		into STRICT	nr_seq_prestador_web_w
		from	pls_guia_plano
		where	nr_sequencia	= nr_seq_guia_p;
	exception
	when others then
		nr_seq_prestador_web_w	:= null;
	end;

elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then


	begin
		select	nr_seq_prestador_web
		into STRICT	nr_seq_prestador_web_w
		from	pls_requisicao
		where	nr_sequencia 	= nr_seq_requisicao_p;
	exception
	when others then
		nr_seq_prestador_web_w	:= null;
	end;

elsif (nr_seq_execucao_p IS NOT NULL AND nr_seq_execucao_p::text <> '') then
	begin
		select	nr_seq_lote_exec
		into STRICT	nr_seq_lote_exec_w
		from	pls_execucao_requisicao
		where	nr_sequencia	= nr_seq_execucao_p;
	exception
	when others then
		nr_seq_lote_exec_w	:= null;
	end;

	if (nr_seq_lote_exec_w IS NOT NULL AND nr_seq_lote_exec_w::text <> '') then
		begin
			select	nr_seq_usuario_web
			into STRICT	nr_seq_prestador_web_w
			from	pls_lote_execucao_req
			where	nr_sequencia	= nr_seq_lote_exec_w;
		exception
		when others then
			nr_seq_prestador_web_w	:= null;
		end;
	end if;

end if;

begin
	select	nr_seq_local_atend
	into STRICT	nr_seq_local_atend_web_w
	from	pls_usuario_web
	where	nr_sequencia	= nr_seq_prestador_web_w;
exception
when others then
	nr_seq_local_atend_web_w 	:= null;
end;

open C01;
loop
fetch C01 into
	nr_seq_usuario_web_filtro_w,
	nr_seq_local_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_prestador_web_w 	= nr_seq_usuario_web_filtro_w)
	or (nr_seq_local_atend_web_w 	= nr_seq_local_atend_w)	then
		ie_gerar_ocorrencia_w 	:= 'S';
	end if;
	end;
end loop;
close C01;

if (ie_gerar_ocorrencia_w = 'S') then
	ie_regra_com_itens_w  :=  pls_obter_se_oco_aut_fil_itens(nr_seq_ocor_filtro_p);

	if (ie_regra_com_itens_w = 'S') then
		ie_tipo_ocorrencia_p := 'I';
	else
		ie_tipo_ocorrencia_p := 'C';
	end if;
end if;

ie_gerar_ocorrencia_p	:= ie_gerar_ocorrencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
