SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_oc_concorrente_exec ( nr_seq_regra_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_execucao_p bigint, ie_concorrente_anterior_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ie_gerar_ocorrencia_p INOUT text) AS $body$
DECLARE


qt_procedimentos_principal_w	bigint;
qt_procedimentos_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ie_gera_ocorrencia_w		varchar(1);
nr_seq_segurado_w		bigint;
dt_execucao_w			timestamp;
nr_seq_exec_req_item_w		bigint;
nr_seq_requisicao_w		bigint;
qt_reg_w			bigint;
nr_seq_proc_w			bigint;
nr_seq_proc_min_w		bigint;
cd_proc_w			varchar(10);
qt_proc_w			bigint;

C01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced
	from	pls_proc_concorrente
	where	nr_seq_regra 		= nr_seq_regra_p
	and	ie_ocorrencia		= 'S'
	and	ie_situacao		= 'A';

C02 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_execucao_req_item a,
		pls_execucao_requisicao b,
		pls_requisicao	c
	where	a.nr_seq_execucao	= b.nr_sequencia
	and	a.nr_seq_execucao	<> nr_seq_execucao_p
	and	c.nr_sequencia		= b.nr_seq_requisicao
	and	c.nr_seq_segurado	= nr_seq_segurado_w
	and	a.cd_procedimento 	= cd_procedimento_w
	and	a.ie_origem_proced	= ie_origem_proced_w
	and	a.ie_situacao		= 'S'
	and	trunc(b.dt_execucao)	= trunc(fim_dia(dt_execucao_w));


BEGIN

/*Obter quantos procedimentos principais existem*/

select	count(a.nr_sequencia)
into STRICT	qt_procedimentos_principal_w
from	pls_proc_concorrente a
where	a.nr_seq_regra 	= nr_seq_regra_p
and	a.ie_principal	= 'S'
and	a.ie_situacao	= 'A'
and	exists (SELECT		1
		 from		pls_execucao_req_item x
		 where		x.nr_seq_execucao	= nr_seq_execucao_p
		 and		x.cd_procedimento 	= a.cd_procedimento
		 and		x.ie_origem_proced	= a.ie_origem_proced);

/*Se existir ao menos um procedimento principal na conta*/

if (qt_procedimentos_principal_w > 0) then

	select	count(a.nr_sequencia)
	into STRICT	qt_procedimentos_w
	from	pls_proc_concorrente a
	where	a.nr_seq_regra 	= nr_seq_regra_p
	and	a.ie_principal	= 'N'
	and	a.ie_situacao	= 'A'
	and	exists (SELECT		1
			 from		pls_execucao_req_item x
			 where		x.nr_seq_execucao	= nr_seq_execucao_p
			 and		x.cd_procedimento 	= a.cd_procedimento
			 and		x.ie_origem_proced	= a.ie_origem_proced);

	/*Se haver ao menor um procedimento principal com um não principal  é gerado ocorrencia*/

	if (qt_procedimentos_w > 0) then
		ie_gera_ocorrencia_w := 'S';

		/*Se for uma verificação de itens é verificado se deve gerar ocorrencia naquele item*/

		if (coalesce(cd_procedimento_p,0) > 0) then

			select	CASE WHEN count(a.nr_sequencia)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_gera_ocorrencia_w
			from	pls_proc_concorrente a
			where	a.nr_seq_regra  = nr_seq_regra_p
			and	a.ie_ocorrencia = 'S'
			and	a.ie_situacao	= 'A'
			and	a.cd_procedimento	= cd_procedimento_p
			and	a.ie_origem_proced	= ie_origem_proced_p;
		end if;
	end if;
else
	if (ie_concorrente_anterior_p	= 'N') then
		select	count(a.nr_sequencia)
		into STRICT	qt_procedimentos_w
		from	pls_proc_concorrente a
		where	a.nr_seq_regra 	= nr_seq_regra_p
		and	a.ie_ocorrencia	= 'S'
		and	a.ie_situacao	= 'A'
		and	exists (SELECT		1
				 from		pls_execucao_req_item x
				 where		x.nr_seq_execucao	= nr_seq_execucao_p
				 and		x.cd_procedimento 	= a.cd_procedimento
				 and		x.ie_origem_proced	= a.ie_origem_proced);

		if (qt_procedimentos_w > 1) then
			ie_gera_ocorrencia_w := 'S';

			/*Se for uma verificação de itens é verificado se deve gerar ocorrencia naquele item*/

			if (coalesce(cd_procedimento_p,0) > 0) then

				select	count(1)
				into STRICT	qt_reg_w
				from	pls_proc_concorrente a
				where	a.nr_seq_regra  = nr_seq_regra_p
				and	a.ie_ocorrencia = 'S'
				and	a.ie_situacao	= 'A'
				and	a.cd_procedimento	= cd_procedimento_p
				and	a.ie_origem_proced	= ie_origem_proced_p;

				nr_seq_proc_min_w	:= pls_obter_seq_proc_min_concorr(nr_seq_regra_p, null, null, nr_seq_execucao_p);

				begin
					select	cd_procedimento,
						sum(qt_item),
						max(nr_sequencia)
					into STRICT	cd_proc_w,
						qt_proc_w,
						nr_seq_proc_w
					from	pls_execucao_req_item
					where	nr_seq_execucao = nr_seq_execucao_p
					group by
						cd_procedimento;
				exception
				when others then
					cd_proc_w	:= null;
					qt_proc_w	:= null;
					nr_seq_proc_w	:= null;
				end;

				if (qt_reg_w > 0) then
					ie_gera_ocorrencia_w := 'S';
				else
					ie_gera_ocorrencia_w := 'N';
				end if;

				if (nr_seq_proc_min_w = nr_seq_proc_w) then
					ie_gera_ocorrencia_w := 'N';
				end if;
			end if;
		end if;

	elsif (ie_concorrente_anterior_p	= 'S') then
		begin
			select	nr_seq_requisicao
			into STRICT	nr_seq_requisicao_w
			from	pls_execucao_requisicao
			where	nr_sequencia	= nr_seq_execucao_p;
		exception
		when others then
			nr_seq_requisicao_w	:= null;
		end;

		if (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
			begin
				select	nr_seq_segurado,
					dt_requisicao
				into STRICT	nr_seq_segurado_w,
					dt_execucao_w
				from	pls_requisicao
				where	nr_sequencia	= nr_seq_requisicao_w;
			exception
			when others then
				nr_seq_segurado_w	:= 0;
			end;
		end if;

		select	count(a.nr_sequencia)
		into STRICT	qt_procedimentos_w
		from	pls_proc_concorrente a
		where	a.nr_seq_regra 		= nr_seq_regra_p
		and	a.ie_ocorrencia		= 'S'
		and	a.ie_situacao		= 'A'
		and	a.cd_procedimento 	= cd_procedimento_p
		and	a.ie_origem_proced	= ie_origem_proced_p;

		if (qt_procedimentos_w	> 0) then
			qt_procedimentos_w	:= 0;
			open C01;
			loop
			fetch C01 into
				cd_procedimento_w,
				ie_origem_proced_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin
				open C02;
				loop
				fetch C02 into
					nr_seq_exec_req_item_w;
				EXIT WHEN NOT FOUND; /* apply on C02 */
					begin
					select	count(1)
					into STRICT	qt_procedimentos_w
					from	pls_execucao_req_item
					where	nr_sequencia		= nr_seq_exec_req_item_w
					and	cd_procedimento 	= cd_procedimento_p
					and	ie_origem_proced	= ie_origem_proced_p;

					if (qt_procedimentos_w = 0) then
						ie_gera_ocorrencia_w := 'S';
					end if;
					end;
				end loop;
				close C02;
				end;
			end loop;
			close C01;
		end if;
	end if;
end if;

ie_gerar_ocorrencia_p := ie_gera_ocorrencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_oc_concorrente_exec ( nr_seq_regra_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_execucao_p bigint, ie_concorrente_anterior_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ie_gerar_ocorrencia_p INOUT text) FROM PUBLIC;

