SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_ops_congenere_benef ( nr_seq_segurado_p bigint, cd_usuario_plano_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


cd_operadora_empresa_w		bigint;
nr_seq_ops_congenere_w		bigint;
nr_seq_congenere_w		bigint;
nr_seq_congenere_aux_w		bigint;
ie_tipo_congenere_w		varchar(2);
cd_usuario_plano_w		varchar(30);
nr_seq_pj_empresa_w		bigint;
qt_registros_w			bigint;
cd_cooperativa_w		varchar(10);

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		a.nr_sequencia
	from	pls_pj_empresa	a,
		pls_congenere	b
	where	a.cd_cgc	= b.cd_cgc
	and	a.cd_empresa	= cd_operadora_empresa_w;


BEGIN

select	max(nr_seq_congenere)
into STRICT	nr_seq_congenere_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (cd_usuario_plano_p IS NOT NULL AND cd_usuario_plano_p::text <> '') then
	cd_usuario_plano_w	:= cd_usuario_plano_p;
else
	select	max(cd_usuario_plano)
	into STRICT	cd_usuario_plano_w
	from	pls_segurado_carteira
	where	nr_seq_segurado	= nr_seq_segurado_p;
end if;

/*aaschlote 10/03/2014 - OS 700610*/

if (length(cd_usuario_plano_w) = 17) then
	cd_cooperativa_w	:= substr(cd_usuario_plano_w,1,4);
	select	max(nr_sequencia)
	into STRICT	nr_seq_congenere_aux_w
	from	pls_congenere
	where	(cd_cooperativa)::numeric  = (cd_cooperativa_w)::numeric;
end if;

cd_operadora_empresa_w	:= pls_obter_cod_empresa_cart(cd_usuario_plano_w, cd_estabelecimento_p);

if (coalesce(cd_operadora_empresa_w,0) <> 0) then
	open C01;
	loop
	fetch C01 into
		nr_seq_ops_congenere_w,
		nr_seq_pj_empresa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		/*aaschlote 05/04/2013 OS - 571004*/

		select	count(1)
		into STRICT	qt_registros_w
		from	PLS_PJ_EMPRESA_COOPERATIVA
		where	NR_SEQ_COD_PJ_EMPRESA	= nr_seq_pj_empresa_w  LIMIT 1;

		if (qt_registros_w	> 0) then
			select	count(1)
			into STRICT	qt_registros_w
			from	PLS_PJ_EMPRESA_COOPERATIVA
			where	NR_SEQ_COD_PJ_EMPRESA	= nr_seq_pj_empresa_w
			and	nr_seq_congenere	= nr_seq_congenere_aux_w  LIMIT 1;

			if (qt_registros_w	= 0) then
				nr_seq_ops_congenere_w	:= null;
			end if;
		end if;

		end;
	end loop;
	close C01;

	if (coalesce(nr_seq_ops_congenere_w,0) <> 0) then
		update	pls_segurado
		set	nr_seq_ops_congenere	= nr_seq_ops_congenere_w,
			cd_operadora_empresa	= cd_operadora_empresa_w
		where	nr_sequencia		= nr_seq_segurado_p;
	end if;
else
	select	max(ie_tipo_congenere)
	into STRICT	ie_tipo_congenere_w
	from	pls_congenere
	where	nr_sequencia	= nr_seq_congenere_w;

	if (ie_tipo_congenere_w = 'OP') then
		update	pls_segurado
		set	nr_seq_ops_congenere	= nr_seq_congenere_w
		where	nr_sequencia		= nr_seq_segurado_p;
	end if;
end if;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
