SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_pagamento_lote ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prestador_w		bigint;
vl_titulo_w			double precision;
cd_estabelecimento_w		smallint;

C01 CURSOR FOR
	SELECT	b.nr_seq_prestador,
		coalesce(sum(b.vl_titulo),0)
	from	pls_prot_conta_titulo	b,
		pls_protocolo_conta	a
	where	a.nr_sequencia		= b.nr_seq_protocolo
	and	a.nr_seq_lote		= nr_seq_lote_p
	group by b.nr_seq_prestador;


BEGIN

delete	from pls_lote_prot_titulo
where	nr_seq_lote	= nr_seq_lote_p;

/* Obter o estabelecimento do lote */

select	coalesce(max(cd_estabelecimento),1)
into STRICT	cd_estabelecimento_w
from	pls_lote_protocolo
where	nr_sequencia	= nr_seq_lote_p;

open C01;
loop
fetch C01 into
	nr_seq_prestador_w,
	vl_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into pls_lote_prot_titulo(nr_sequencia,
		cd_estabelecimento,
		nr_seq_lote,
		nr_seq_prestador,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		vl_titulo,
		nr_nota_fiscal)
	values (	nextval('pls_lote_prot_titulo_seq'),
		cd_estabelecimento_w,
		nr_seq_lote_p,
		nr_seq_prestador_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		vl_titulo_w,
		null);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_pagamento_lote ( nr_seq_lote_p bigint, nm_usuario_p text) FROM PUBLIC;

