CREATE OR REPLACE PROCEDURE pls_gerar_parcelas_agravo ( nr_seq_segurado_agravo_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p
	G - Gerar
	D - Desfazer
*/
nr_seq_agravo_w			bigint;
qt_parcelas_w			bigint;
vl_agravo_total_w		double precision;
vl_agravo_w			double precision;
nr_parcela_w			bigint	:= 1;
nr_seq_segurado_w		bigint;
dt_referencia_w			timestamp;
qt_parcelas_existentes_w	bigint;
vl_diferenca_w			double precision;
qt_mens_geradas_w		bigint;
tx_agravo_w			double precision;


BEGIN

if (ie_opcao_p = 'G') then
	select	nr_seq_agravo,
		nr_seq_segurado,
		qt_parcelas,
		vl_agravo,
		tx_agravo
	into STRICT	nr_seq_agravo_w,
		nr_seq_segurado_w,
		qt_parcelas_w,
		vl_agravo_total_w,
		tx_agravo_w
	from	pls_segurado_agravo
	where	nr_sequencia	= nr_seq_segurado_agravo_p;

	if (coalesce(qt_parcelas_w,0) = 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(293555);
	end if;

	select	add_months(max(c.dt_referencia),1)
	into STRICT	dt_referencia_w
	from	pls_segurado		a,
		pls_contrato_pagador	b,
		pls_mensalidade		c
	where	a.nr_seq_pagador	= b.nr_sequencia
	and	c.nr_seq_pagador	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_segurado_w;

	if (coalesce(dt_referencia_w::text, '') = '') then
		select	coalesce(max(dt_contratacao),clock_timestamp())
		into STRICT	dt_referencia_w
		from	pls_segurado
		where	nr_sequencia	= nr_seq_segurado_w;
	end if;

	vl_agravo_w	:= vl_agravo_total_w / qt_parcelas_w;

	vl_diferenca_w	:= vl_agravo_total_w - (qt_parcelas_w * vl_agravo_w);

	select	count(1)
	into STRICT	qt_parcelas_existentes_w
	from	pls_segurado_agravo_parc
	where	nr_seq_segurado_agravo	= nr_seq_segurado_agravo_p;

	if (qt_parcelas_existentes_w = 0) then
		while(nr_parcela_w <= qt_parcelas_w) loop
			begin

			if	(nr_parcela_w = qt_parcelas_w AND vl_diferenca_w <> 0) then
				vl_agravo_w	:= vl_agravo_w + vl_diferenca_w;
			end if;

			insert	into	pls_segurado_agravo_parc(	nr_sequencia, nr_seq_segurado_agravo, cd_estabelecimento,
					dt_mes_competencia, vl_agravo, nr_parcela,
					dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
					nm_usuario_nrec,tx_agravo)
				values (	nextval('pls_segurado_agravo_parc_seq'), nr_seq_segurado_agravo_p, cd_estabelecimento_p,
					dt_referencia_w, vl_agravo_w, nr_parcela_w,
					clock_timestamp(), nm_usuario_p, clock_timestamp(),
					nm_usuario_p,tx_agravo_w);

			nr_parcela_w := nr_parcela_w + 1;
			dt_referencia_w	:= add_months(dt_referencia_w,1);
			end;
		end loop;
	end if;
elsif (ie_opcao_p = 'D') then
	select	count(1)
	into STRICT	qt_mens_geradas_w
	from	pls_segurado_agravo_parc
	where	nr_seq_segurado_agravo	= nr_seq_segurado_agravo_p
	and	(nr_seq_mensalidade_item IS NOT NULL AND nr_seq_mensalidade_item::text <> '');

	if (coalesce(qt_mens_geradas_w,0) <> 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(262242);
		/* Mensagem: Não é possível desfazer as parcelas! Já foi gerada uma mensalidade para o agravo, favor verificar! */

	else
		delete	from	pls_segurado_agravo_parc
		where	nr_seq_segurado_agravo	= nr_seq_segurado_agravo_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_parcelas_agravo ( nr_seq_segurado_agravo_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

