SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_parc_agravo_analise ( nr_seq_analise_agravo_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p
	G - Gerar
	D - Desfazer
*/
nr_seq_agravo_w			bigint;
qt_parcelas_w			bigint;
vl_agravo_total_w		double precision;
vl_agravo_w			double precision;
nr_parcela_w			bigint	:= 1;
dt_referencia_w			timestamp;
qt_parcelas_existentes_w	bigint;
vl_diferenca_w			double precision;
qt_mens_geradas_w		bigint;
qt_parcelas_atual_w		bigint;
tx_agravo_w			double precision;
ie_tipo_agravo_w		varchar(2);
qt_regras_agravo_w		bigint;
qt_criterios_agravo_w		bigint;


BEGIN

if (ie_opcao_p = 'G') then
	select	nr_seq_agravo,
		vl_agravo,
		qt_parcelas_agravo,
		tx_agravo,
		ie_tipo_agravo
	into STRICT	nr_seq_agravo_w,
		vl_agravo_total_w,
		qt_parcelas_w,
		tx_agravo_w,
		ie_tipo_agravo_w
	from	pls_analise_agravo
	where	nr_sequencia	= nr_seq_analise_agravo_p;
	
	if (coalesce(qt_parcelas_w,0) = 0) then
		select	count(1)
		into STRICT	qt_regras_agravo_w
		from	pls_agravo a,
			pls_agravo_lote_valor b
		where	a.nr_sequencia = nr_seq_agravo_w
		and	b.nr_seq_agravo = a.nr_sequencia
		and	coalesce(b.ie_classificacao, '0') = coalesce(ie_tipo_agravo_w, '0')
		and	clock_timestamp() between b.dt_inicio_vigencia and coalesce(b.dt_fim_vigencia, clock_timestamp());
		
		if (qt_regras_agravo_w > 1) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1061818);
		end if;
		
		select	count(1)
		into STRICT	qt_criterios_agravo_w
		from	pls_agravo a,
			pls_agravo_lote_valor b,
			pls_agravo_valor c
		where	a.nr_sequencia	= b.nr_seq_agravo
		and	b.nr_sequencia	= c.nr_seq_lote
		and	a.nr_sequencia	= nr_seq_agravo_w
		and	clock_timestamp() between b.dt_inicio_vigencia and coalesce(b.dt_fim_vigencia, clock_timestamp());
		
		if (qt_criterios_agravo_w > 1) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1061817);
		end if;
		
		select	max(c.vl_agravo),
			max(c.qt_parcelas),
			max(c.tx_agravo)
		into STRICT	vl_agravo_total_w,
			qt_parcelas_w,
			tx_agravo_w
		from	pls_agravo a,
			pls_agravo_lote_valor b,
			pls_agravo_valor c
		where	a.nr_sequencia	= b.nr_seq_agravo
		and	b.nr_sequencia	= c.nr_seq_lote
		and	a.nr_sequencia	= nr_seq_agravo_w
		and	clock_timestamp() between b.dt_inicio_vigencia and coalesce(b.dt_fim_vigencia, clock_timestamp());
		
		if (coalesce(qt_parcelas_w,0) = 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(294044);
		end if;
	end if;
	
	select	count(nr_parcela_agravo)
	into STRICT	qt_parcelas_atual_w
	from	pls_agravo_parcela
	where	nr_seq_analise_agravo = nr_seq_analise_agravo_p;
	
	vl_agravo_w	:= vl_agravo_total_w / qt_parcelas_w;
	
	vl_diferenca_w	:= vl_agravo_total_w - (qt_parcelas_w * vl_agravo_w);
	
	select	count(1)
	into STRICT	qt_parcelas_existentes_w
	from	pls_agravo_parcela
	where	nr_seq_analise_agravo	= nr_seq_analise_agravo_p
	and	qt_parcelas_atual_w	= qt_parcelas_w;
	
	if (qt_parcelas_existentes_w = 0) then
		delete	FROM pls_agravo_parcela
		where	nr_seq_analise_agravo	= nr_seq_analise_agravo_p;
		while(nr_parcela_w <= qt_parcelas_w) loop
			begin
			
			if	(nr_parcela_w = qt_parcelas_w AND vl_diferenca_w <> 0) then
				vl_agravo_w	:= vl_agravo_w + vl_diferenca_w;
			end if;
			
			insert	into	pls_agravo_parcela(	nr_sequencia, nr_seq_analise_agravo, cd_estabelecimento,
					vl_parcela, nr_parcela_agravo, dt_atualizacao,
					nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,tx_parcela)
				values (	nextval('pls_agravo_parcela_seq'), nr_seq_analise_agravo_p, cd_estabelecimento_p,
					vl_agravo_w, nr_parcela_w, clock_timestamp(),
					nm_usuario_p, clock_timestamp(), nm_usuario_p,tx_agravo_w);
			
			nr_parcela_w := nr_parcela_w + 1;
			end;
		end loop;
	end if;
elsif (ie_opcao_p = 'D') then
	delete	FROM pls_agravo_parcela
	where	nr_seq_analise_agravo	= nr_seq_analise_agravo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
