CREATE OR REPLACE PROCEDURE pls_gerar_partic_proc_web ( nr_sequencia_p bigint, cd_medico_p text, nr_seq_prestador_p bigint, nr_seq_grau_partic_p bigint, nm_usuario_p text, vl_complemento_p bigint, nr_seq_cbo_saude_p bigint, ie_acao_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Gerar o participante no complemento de contas médicas
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

	IE_ACAO_P

	U - Update
	I - Insert
*/BEGIN

if (ie_acao_p = 'U' and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')) then

	update  pls_proc_participante
	set	cd_medico = cd_medico_p,
		nr_seq_prestador = nr_seq_prestador_p,
		nr_seq_grau_partic = nr_seq_grau_partic_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		vl_digitado_complemento = vl_complemento_p,
		nr_seq_cbo_saude = nr_seq_cbo_saude_p
	where   nr_sequencia = nr_sequencia_p;

elsif (ie_acao_p = 'I' and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')) then

	insert into pls_proc_participante(	nr_sequencia, nr_seq_conta_proc , cd_medico,
			nr_seq_prestador, nr_seq_grau_partic, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			vl_digitado_complemento, nr_seq_cbo_saude)
	values (	nextval('pls_proc_participante_seq'), nr_sequencia_p, cd_medico_p,
			nr_seq_prestador_p, nr_seq_grau_partic_p, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			vl_complemento_p, nr_seq_cbo_saude_p);

end if;

commit;

end;
$body$
