CREATE OR REPLACE PROCEDURE pls_gerar_pericia_medica ( nr_seq_pessoa_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proposta_w		bigint;
cd_pessoa_fisica_w		varchar(10);
nr_seq_plano_w			bigint;
nr_seq_pagador_w		bigint;
nr_seq_segurado_w		bigint;
cd_estabelecimento_w		smallint;


BEGIN

/* Obter dados dados da pessoa da proposta */

select	a.nr_seq_proposta,
	a.cd_beneficiario,
	a.nr_seq_plano,
	a.nr_seq_pagador,
	b.cd_estabelecimento
into STRICT	nr_seq_proposta_w,
	cd_pessoa_fisica_w,
	nr_seq_plano_w,
	nr_seq_pagador_w,
	cd_estabelecimento_w
from	pls_proposta_adesao		b,
	pls_proposta_beneficiario	a
where	a.nr_seq_proposta		= b.nr_sequencia
and	a.nr_sequencia			= nr_seq_pessoa_proposta_p;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_segurado_w
from	pls_segurado
where	ie_tipo_segurado	= 'P'
and	nr_proposta_adesao	= nr_seq_proposta_w;

if (nr_seq_segurado_w	= 0) then
	select	nextval('pls_segurado_seq')
	into STRICT	nr_seq_segurado_w
	;

	insert into pls_segurado(
		nr_sequencia, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, cd_pessoa_fisica,
		nr_seq_titular, nr_seq_contrato, nr_seq_pagador,
		nr_seq_plano, nr_seq_tabela, nr_proposta_adesao,
		ie_tipo_segurado, ie_situacao_atend,cd_estabelecimento)
	values (	nr_seq_segurado_w, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, cd_pessoa_fisica_w,
		null, null, null,
		nr_seq_plano_w, null, nr_seq_proposta_w,
		'P','A',cd_estabelecimento_w);
end if;

insert into pls_pericia_medica(
	nr_sequencia, cd_estabelecimento, dt_pericia,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
	nm_usuario_nrec, nr_seq_segurado, ie_status,
	dt_liberacao, nr_seq_emissor, nr_seq_prestador,
	ie_tipo_rede_atendimento, cd_medico_executor)
values (	nextval('pls_pericia_medica_seq'), cd_estabelecimento_w, clock_timestamp(),
	clock_timestamp(), nm_usuario_p, clock_timestamp(),
	nm_usuario_p, nr_seq_segurado_w, 'U',
	null, null, null,
	null, null);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
