SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_portab_proposta ( nr_seq_proposta_benef_p pls_proposta_beneficiario.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_portabilidade_p INOUT bigint) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(100);
nr_seq_plano_w			bigint;
nr_seq_tabela_w			bigint;
nr_seq_portabilidade_w		bigint;
qt_dias_resposta_w		bigint;
nr_seq_protocolo_atend_w	pls_protocolo_atendimento.nr_sequencia%type;
nr_protocolo_atend_w		pls_protocolo_atendimento.nr_protocolo%type;
nr_seq_inclusao_benef_w		pls_proposta_beneficiario.nr_seq_inclusao_benef%type;
ie_origem_solicitacao_w		pls_portab_pessoa.ie_origem_solicitacao%type;	
dt_inclusao_ops_origem_w	pls_inclusao_beneficiario.dt_inclusao_ops_origem%type;
vl_preco_plano_origem_w		pls_inclusao_beneficiario.vl_preco_plano_origem%type;
nr_protocolo_portabilidade_w	pls_inclusao_beneficiario.nr_protocolo_portabilidade%type;
ie_abragencia_plano_origem_w	pls_inclusao_beneficiario.ie_abrangencia_plano_origem%type;
nr_registro_plano_origem_w	pls_inclusao_beneficiario.nr_registro_plano_origem%type;
ie_tp_contratacao_plano_orig_w	pls_inclusao_beneficiario.ie_tp_contratacao_plano_origem%type;
nr_seq_portab_operadora_w	pls_portab_operadora.nr_sequencia%type;
dt_emissao_prot_portab_w	pls_inclusao_beneficiario.dt_emissao_prot_portab%type;


BEGIN

if (coalesce(nr_seq_proposta_benef_p,0) > 0) then
	qt_dias_resposta_w	:= coalesce(obter_valor_param_usuario(1242, 1, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p),0);
	
	select	cd_beneficiario,
		nr_seq_plano,
		nr_seq_tabela,
		nr_seq_inclusao_benef
	into STRICT	cd_pessoa_fisica_w,
		nr_seq_plano_w,
		nr_seq_tabela_w,
		nr_seq_inclusao_benef_w
	from	pls_proposta_beneficiario
	where	nr_sequencia = nr_seq_proposta_benef_p;	
	
	if (nr_seq_inclusao_benef_w IS NOT NULL AND nr_seq_inclusao_benef_w::text <> '') then
		select	a.dt_inclusao_ops_origem,
			a.vl_preco_plano_origem,
			a.nr_protocolo_portabilidade,
			a.dt_emissao_prot_portab,
			(select	max(x.nr_sequencia)
			from	pls_portab_operadora x
			where	a.nr_registro_ops_ans = x.cd_ans),
			a.ie_abrangencia_plano_origem,
			a.nr_registro_plano_origem,
			a.ie_tp_contratacao_plano_origem
		into STRICT	dt_inclusao_ops_origem_w,
			vl_preco_plano_origem_w, 
			nr_protocolo_portabilidade_w,
			dt_emissao_prot_portab_w,
			nr_seq_portab_operadora_w,
			ie_abragencia_plano_origem_w,
			nr_registro_plano_origem_w,
			ie_tp_contratacao_plano_orig_w
		from	pls_inclusao_beneficiario a
		where	a.nr_sequencia = nr_seq_inclusao_benef_w;
	end if;
	
	ie_origem_solicitacao_w := 'T';
	
	if (nr_seq_inclusao_benef_w IS NOT NULL AND nr_seq_inclusao_benef_w::text <> '') then
		ie_origem_solicitacao_w := 'P';
	end if;
	
	insert into pls_portab_pessoa(	nr_sequencia,
					cd_pessoa_fisica,
					dt_solicitacao,
					dt_resposta,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_plano,
					nr_seq_tabela,
					ie_status,
					ie_adimplente,
					cd_estabelecimento,
					ie_origem_solicitacao,
					dt_contratacao_origem,
					vl_preco_origem,
					nr_protocolo_portabilidade,
					dt_emissao_protocolo,
					ie_abrangencia,
					nr_prot_ans_origem,
					ie_tipo_contratacao,
					nr_seq_portab_operadora)
				values (	nextval('pls_portab_pessoa_seq'),
					cd_pessoa_fisica_w,
					clock_timestamp(),
					clock_timestamp()+qt_dias_resposta_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_plano_w,
					nr_seq_tabela_w,
					'U',
					'S',
					cd_estabelecimento_p,
					ie_origem_solicitacao_w,
					dt_inclusao_ops_origem_w,
					vl_preco_plano_origem_w,
					nr_protocolo_portabilidade_w,
					dt_emissao_prot_portab_w,
					ie_abragencia_plano_origem_w,
					nr_registro_plano_origem_w,
					ie_tp_contratacao_plano_orig_w,
					nr_seq_portab_operadora_w)
			returning nr_sequencia into nr_seq_portabilidade_w;
	
	SELECT * FROM pls_gravar_protocolo_atend('10', null, null, null, null, null, null, null, nr_seq_portabilidade_w, null, cd_estabelecimento_p, nm_usuario_p, nr_seq_protocolo_atend_w, nr_protocolo_atend_w) INTO STRICT nr_seq_protocolo_atend_w, nr_protocolo_atend_w;
	
	update	pls_proposta_beneficiario
	set	nr_seq_portabilidade	= nr_seq_portabilidade_w
	where	nr_sequencia		= nr_seq_proposta_benef_p;
	
	nr_seq_portabilidade_p	:= nr_seq_portabilidade_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_portab_proposta ( nr_seq_proposta_benef_p pls_proposta_beneficiario.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_portabilidade_p INOUT bigint) FROM PUBLIC;

