SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_pos_estab_disc ( nr_seq_lote_disc_p pls_lote_discussao.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


ie_tipo_arquivo_w		pls_lote_discussao.ie_tipo_arquivo%type;
qt_item_pos_estab_w		integer;
nr_seq_analise_w		pls_analise_conta.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	a.nr_seq_conta,
		c.nr_seq_conta_proc,
		null nr_seq_conta_mat
	from	pls_conta		x,
		pls_conta_proc		d,
		pls_discussao_proc 	c,
		pls_contestacao		a,
		pls_contestacao_discussao b
	where	b.nr_seq_contestacao	= a.nr_sequencia
	and	c.nr_seq_discussao	= b.nr_sequencia
	and	b.nr_seq_lote		= nr_seq_lote_disc_p
	and	d.nr_sequencia		= c.nr_seq_conta_proc
	and	x.nr_sequencia		= a.nr_seq_conta
	and	(x.nr_seq_segurado IS NOT NULL AND x.nr_seq_segurado::text <> '')
	and	((c.qt_aceita		> 0) or ((c.vl_aceito > 0) and ((coalesce(d.qt_procedimento_imp,0) - coalesce(d.qt_procedimento,0)) > 0)))
	and	c.ie_tipo_acordo	in ('01', '02', '05', '10', '12', '13')
	
union all

	SELECT	a.nr_seq_conta,
		null nr_seq_conta_proc,
		c.nr_seq_conta_mat
	from	pls_conta		x,
		pls_conta_mat		d,
		pls_discussao_mat 	c,
		pls_contestacao		a,
		pls_contestacao_discussao b
	where	b.nr_seq_contestacao	= a.nr_sequencia
	and	c.nr_seq_discussao	= b.nr_sequencia
	and	b.nr_seq_lote		= nr_seq_lote_disc_p
	and	d.nr_sequencia		= c.nr_seq_conta_mat
	and	x.nr_sequencia		= a.nr_seq_conta
	and	(x.nr_seq_segurado IS NOT NULL AND x.nr_seq_segurado::text <> '')
	and	((c.qt_aceita		> 0) or ((c.vl_aceito > 0) and ((coalesce(d.qt_material_imp,0) - coalesce(d.qt_material,0)) > 0)))
	and	c.ie_tipo_acordo	in ('01', '02', '05', '10', '12', '13')
	order by 1;

c02 CURSOR FOR
	SELECT  distinct(nr_seq_conta) nr_seq_conta
	from (
	SELECT	a.nr_seq_conta
	from	pls_conta		x,
		pls_conta_proc		d,
		pls_discussao_proc 	c,
		pls_contestacao		a,
		pls_contestacao_discussao b
	where	b.nr_seq_contestacao	= a.nr_sequencia
	and	c.nr_seq_discussao	= b.nr_sequencia
	and	b.nr_seq_lote		= nr_seq_lote_disc_p
	and	d.nr_sequencia		= c.nr_seq_conta_proc
	and	x.nr_sequencia		= a.nr_seq_conta
	and	(x.nr_seq_segurado IS NOT NULL AND x.nr_seq_segurado::text <> '')
	and	((c.qt_aceita		> 0) or ((c.vl_aceito > 0) and ((coalesce(d.qt_procedimento_imp,0) - coalesce(d.qt_procedimento,0)) > 0)))
	and	c.ie_tipo_acordo	in ('01', '02', '05', '10', '12', '13')
	
union all

	select	a.nr_seq_conta
	from	pls_conta		x,
		pls_conta_mat		d,
		pls_discussao_mat 	c,
		pls_contestacao		a,
		pls_contestacao_discussao b
	where	b.nr_seq_contestacao	= a.nr_sequencia
	and	c.nr_seq_discussao	= b.nr_sequencia
	and	b.nr_seq_lote		= nr_seq_lote_disc_p
	and	d.nr_sequencia		= c.nr_seq_conta_mat
	and	x.nr_sequencia		= a.nr_seq_conta
	and	(x.nr_seq_segurado IS NOT NULL AND x.nr_seq_segurado::text <> '')
	and	((c.qt_aceita		> 0) or ((c.vl_aceito > 0) and ((coalesce(d.qt_material_imp,0) - coalesce(d.qt_material,0)) > 0)))
	and	c.ie_tipo_acordo	in ('01', '02', '05', '10', '12', '13')) alias21

	order by 1;
BEGIN

if (nr_seq_lote_disc_p IS NOT NULL AND nr_seq_lote_disc_p::text <> '') then
	select	ie_tipo_arquivo
	into STRICT	ie_tipo_arquivo_w
	from	pls_lote_discussao
	where	nr_sequencia = nr_seq_lote_disc_p;

	if (ie_tipo_arquivo_w in (3,4,5,6,7,8)) then -- Discuss√£o encerrada
		for r_c01_w in C01() loop
			begin
			CALL pls_gerar_valor_pos_estab( r_c01_w.nr_seq_conta, nm_usuario_p, 'F', r_c01_w.nr_seq_conta_proc, r_c01_w.nr_seq_conta_mat, 'F');
			end;
		end loop;

		for r_c02_w in C02() loop
			begin
			select	count(1)
			into STRICT	qt_item_pos_estab_w
			from	pls_conta_pos_estabelecido a
			where	a.nr_seq_conta	= r_c02_w.nr_seq_conta
			and	((a.ie_situacao	= 'A')			or (coalesce(a.ie_situacao::text, '') = ''))
			and	((a.nr_seq_conta_proc IS NOT NULL AND a.nr_seq_conta_proc::text <> '')	or (a.nr_seq_conta_mat IS NOT NULL AND a.nr_seq_conta_mat::text <> ''))
			and	coalesce(a.nr_seq_analise::text, '') = ''
			and	(a.nr_seq_cabecalho IS NOT NULL AND a.nr_seq_cabecalho::text <> '');

			if (qt_item_pos_estab_w > 0) then
				CALL pls_gerar_analise_pos_estab(r_c02_w.nr_seq_conta, nm_usuario_p, cd_estabelecimento_p,'C', null, 'D');
			end if;

			select	max(nr_sequencia)
			into STRICT	nr_seq_analise_w
			from	pls_analise_conta
			where	nr_sequencia in (SELECT	nr_seq_analise
						from	pls_conta_pos_estabelecido
						where	nr_seq_conta	= r_c02_w.nr_seq_conta
						and	((ie_situacao	= 'A') or (coalesce(ie_situacao::text, '') = '')))
			and	ie_origem_analise = 7;

			CALL pls_fechar_conta_pos_estab(nr_seq_analise_w, nm_usuario_p, cd_estabelecimento_p, 'S', 'N');

			end;
		end loop;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_pos_estab_disc ( nr_seq_lote_disc_p pls_lote_discussao.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

