SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_prazo_impugnacao ( nr_seq_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_recebimento_w		timestamp;
nr_seq_processo_w		bigint;
qt_dias_impugnacao_w		bigint;


BEGIN

select	obter_valor_param_usuario(1238,4,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p)
into STRICT	qt_dias_impugnacao_w
;

begin
	select	a.dt_recebimento,
		a.nr_sequencia
	into STRICT	dt_recebimento_w,
		nr_seq_processo_w
	from	pls_processo a,
		pls_processo_conta b
	where	a.nr_sequencia	= b.nr_seq_processo
	and	b.nr_sequencia	= nr_seq_conta_p;
exception
when others then
	dt_recebimento_w := null;
	nr_seq_processo_w:= null;
end;

if (dt_recebimento_w IS NOT NULL AND dt_recebimento_w::text <> '') then
	update	pls_processo_conta
	set	dt_impugnacao	= (dt_recebimento_w + coalesce(qt_dias_impugnacao_w,0))
	where	nr_sequencia	= nr_seq_conta_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
