CREATE OR REPLACE PROCEDURE pls_gerar_procedimento_pcmso ( nr_seq_estrutura_p bigint, nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
cd_cbo_w			varchar(6);
nr_seq_item_pcmso_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_pcmso_item_estrut
	where	cd_cbo	= cd_cbo_w
	and	nr_seq_estrutura	= nr_seq_estrutura_p;


BEGIN
select	max(nr_seq_segurado)
into STRICT	nr_seq_segurado_w
from	pls_guia_plano
where	nr_Sequencia	= nr_seq_guia_p;

select	max(cd_cbo)
into STRICT	cd_cbo_w
from	pls_segurado_compl
where	nr_seq_segurado	= nr_seq_segurado_w;

open C01;
loop
fetch C01 into
	nr_seq_item_pcmso_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into pls_guia_plano_proc(nr_sequencia, cd_procedimento, ie_origem_proced,
		qt_autorizada, ie_status, dt_atualizacao,
		nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		nr_seq_guia, qt_solicitada )
	(SELECT	nextval('pls_guia_plano_proc_seq'), cd_procedimento, ie_origem_proced,
		1, 'U',clock_timestamp(),
		nm_usuario_p, clock_timestamp(), nm_usuario_p,
		nr_seq_guia_p, 1
	from	pls_pcmso_item_estrut
	where 	nr_Sequencia = nr_seq_item_pcmso_w);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
