CREATE OR REPLACE PROCEDURE pls_gerar_proc_itens_simul ( nr_seq_simul_perfil_p bigint, nr_seq_simul_param_item_p INOUT bigint, ds_procedure_p INOUT text) AS $body$
DECLARE


ds_procedure_calc_w		varchar(255);
qt_registros_w			bigint;
ds_comando_w			varchar(4000);
nr_seq_simul_param_item_w	bigint;
nr_seq_simul_param_item_ww	bigint;
ds_procedure_w			varchar(255);

C01 CURSOR FOR
	SELECT	b.ds_procedure_calc,
		a.nr_sequencia
	from	pls_simul_fator_alt_tipo	b,
		pls_simul_param_item		a
	where	a.nr_seq_tipo_fator		= b.nr_sequencia
	and	a.nr_seq_regra_perfil		= nr_seq_simul_perfil_p
	and	(b.ds_procedure_calc IS NOT NULL AND b.ds_procedure_calc::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	ds_procedure_calc_w,
	nr_seq_simul_param_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_registros_w
	from	user_objects
	where	upper(OBJECT_NAME)	= upper(ds_procedure_calc_w)
	and	object_type		= 'PROCEDURE';

	if (qt_registros_w	> 0) then
		ds_procedure_w	:= ds_procedure_calc_w;
		nr_seq_simul_param_item_ww	:= nr_seq_simul_param_item_w;
	end if;
	end;
end loop;
close C01;

ds_procedure_p			:= ds_procedure_w;
nr_seq_simul_param_item_p	:= nr_seq_simul_param_item_ww;

end;
$body$
