SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_proposta_adesao ( nr_seq_cliente_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_proposta_p INOUT bigint) AS $body$
DECLARE


nr_seq_proposta_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
nr_seq_vendedor_canal_w		bigint;
nr_seq_solicitacao_w		bigint;
ie_tipo_contratacao_w		varchar(2);
cd_empresa_ref_w		bigint;
ds_empresa_ref_w		varchar(255);
nr_seq_empresa_serv_w		bigint;
ds_empresa_serv_w		varchar(255);
ds_observacao_w			varchar(4000);
nr_contrato_w			bigint;
ds_tipo_plano_w			varchar(255);
nr_sequencia_w			bigint;
nr_seq_vendedor_aux_w		bigint;
nr_seq_solic_vendedor_w		bigint;
nr_seq_vendedor_vinculado_w	bigint;
nr_contrato_existente_w		bigint;
ie_tipo_proposta_w		smallint	:= 1;
nr_seq_origem_agente_w		bigint;
nr_seq_agente_motivador_w	bigint;

C01 CURSOR FOR
	SELECT	nr_contrato,
		ds_tipo_plano,
		cd_empresa_ref,
		substr(obter_nome_empresa_ref(cd_empresa_ref),1,255),
		nr_seq_empresa_serv,
		substr(obter_desc_servico_empresa(nr_seq_empresa_serv),1,255),
		ds_observacao
	from	pls_comercial_empresa
	where	nr_seq_cliente = nr_seq_cliente_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_solic_vendedor_compl
	where	nr_seq_cliente = nr_seq_cliente_p;


BEGIN

select	cd_pessoa_fisica,
	cd_cgc,
	nr_seq_solicitacao,
	nr_contrato_existente,
	nr_seq_origem_agente,
	nr_seq_agente_motivador
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_w,
	nr_seq_solicitacao_w,
	nr_contrato_existente_w,
	nr_seq_origem_agente_w,
	nr_seq_agente_motivador_w
from	pls_comercial_cliente
where	nr_sequencia	= nr_seq_cliente_p;

if (nr_contrato_existente_w IS NOT NULL AND nr_contrato_existente_w::text <> '') then
	ie_tipo_proposta_w	:= 2;
else
	ie_tipo_proposta_w	:= 1;
end if;

select	max(nr_sequencia)
into STRICT	nr_seq_solic_vendedor_w
from	pls_solicitacao_vendedor
where	nr_seq_cliente	= nr_seq_cliente_p
and	coalesce(dt_fim_vigencia::text, '') = '';

select	max(nr_seq_vendedor_canal),
	max(nr_seq_vendedor_vinculado)
into STRICT	nr_seq_vendedor_canal_w,
	nr_seq_vendedor_vinculado_w
from	pls_solicitacao_vendedor
where	nr_sequencia	= nr_seq_solic_vendedor_w;

/*Obter o tipo de contratacão para a proposta de adesão*/

select	max(ie_tipo_contratacao)
into STRICT	ie_tipo_contratacao_w
from	pls_solicitacao_comercial
where	nr_sequencia	= nr_seq_solicitacao_w;


--aaschlote 07/04/2011 - Parâmetro[36] - Tipo de consistência ao aprovar solicitação de lead/prospect sem simulação de preço vinculada
CALL pls_consistir_simul_comercial(nr_seq_cliente_p,'P',cd_estabelecimento_p,nm_usuario_p);

select	nextval('pls_proposta_adesao_seq')
into STRICT	nr_seq_proposta_w
;

insert	into	pls_proposta_adesao(	nr_sequencia,
		ie_tipo_contratacao,
		cd_estabelecimento,
		ie_tipo_beneficiario,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_cgc_estipulante,
		cd_estipulante,
		ie_estipulante_pagador,
		dt_inicio_proposta,
		ie_tipo_processo,
		ie_status,
		nr_seq_vendedor_canal,
		nr_seq_vendedor_pf,
		vl_proposta,
		ie_tipo_proposta,
		nr_seq_cliente,
		nr_seq_contrato,
		nr_seq_origem_agente,
		nr_seq_agente_motivador)
	values (	nr_seq_proposta_w,
		ie_tipo_contratacao_w,
		cd_estabelecimento_p,
		'BE',
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_cgc_w,
		cd_pessoa_fisica_w,
		'N',
		clock_timestamp(),
		'M',
		'U',
		nr_seq_vendedor_canal_w,
		nr_seq_vendedor_vinculado_w,
		0,
		ie_tipo_proposta_w,
		nr_seq_cliente_p,
		nr_contrato_existente_w,
		nr_seq_origem_agente_w,
		nr_seq_agente_motivador_w);

CALL pls_proposta_benef_indicacao(nr_seq_proposta_w, null, 'P', 'N', nm_usuario_p, cd_estabelecimento_p);

open C01;
loop
fetch C01 into
	nr_contrato_w,
	ds_tipo_plano_w,
	cd_empresa_ref_w,
	ds_empresa_ref_w,
	nr_seq_empresa_serv_w,
	ds_empresa_serv_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('pls_comercial_empresa_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into	pls_comercial_empresa(	nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_empresa_serv,
			ds_observacao,
			cd_empresa_ref,
			nr_seq_proposta,
			nr_contrato,
			dt_inicio_vigencia,
			ds_tipo_plano)
		values (	nr_sequencia_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_empresa_serv_w,
			ds_observacao_w,
			cd_empresa_ref_w,
			nr_seq_proposta_w,
			nr_contrato_w,
			clock_timestamp(),
			ds_tipo_plano_w);
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_vendedor_aux_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	insert	into	pls_benef_prop_canal_compl(	nr_sequencia,
			nr_seq_proposta,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_vendedor_canal,
			nr_seq_vendedor_vinculado)
		(SELECT	nextval('pls_benef_prop_canal_compl_seq'),
			nr_seq_proposta_w,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_vendedor_canal,
			nr_seq_vendedor_vinculado
		from	pls_solic_vendedor_compl
		where	nr_sequencia	= nr_seq_vendedor_aux_w);
	end;
end loop;
close C02;

insert	into	pls_proposta_anexo(	nr_sequencia,
		nr_seq_proposta,
		nm_usuario,
		nm_usuario_nrec,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_anexo,
		ds_titulo,
		ds_anexo)
(	SELECT	nextval('pls_proposta_anexo_seq'),
		nr_seq_proposta_w,
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp(),
		dt_atualizacao_nrec,
		ds_titulo,
		ds_arquivo
	from	pls_comercial_documento
	where	nr_seq_cliente	= nr_seq_cliente_p);

update	pls_comercial_cliente
set	dt_proposta		= clock_timestamp(),
	ie_classificacao	= 'B',
	ie_fase_venda		= 'PA',
	ie_status		= 'C'
where	nr_sequencia		= nr_seq_cliente_p;

nr_seq_proposta_p	:= nr_seq_proposta_w;

--pls_atualizar_status_cliente(nr_seq_cliente_p,nm_usuario_p);
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerar_proposta_adesao ( nr_seq_cliente_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_proposta_p INOUT bigint) FROM PUBLIC;

