SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gerar_proposta_comercial ( nr_proposta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cliente_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
nr_seq_vendedor_canal_w 	bigint;
nr_seq_vendedor_vinculado_w 	bigint;
qt_nr_cliente_w			smallint;


BEGIN
select  cd_cgc_estipulante,
	cd_estipulante,
	nr_seq_vendedor_canal,
	nr_seq_vendedor_pf
into STRICT	cd_cgc_w,
	cd_pessoa_fisica_w,
	nr_seq_vendedor_canal_w,
	nr_seq_vendedor_vinculado_w
from    pls_proposta_adesao
where   nr_sequencia = nr_proposta_p;

select 	nextval('pls_comercial_cliente_seq')
into STRICT	nr_seq_cliente_w
;

select  count(b.nr_seq_cliente)
into STRICT	qt_nr_cliente_w
from    pls_comercial_cliente a,
        pls_proposta_adesao b
where   a.nr_sequencia = b.nr_seq_cliente
and     b.nr_sequencia = nr_proposta_p;

if (qt_nr_cliente_w = 0) then
	insert into pls_comercial_cliente(
		nr_sequencia,
		ie_status,
		ie_fase_venda,
		dt_aprovacao,
		dt_proposta,
		ie_classificacao,
		cd_pessoa_fisica,
		cd_cgc,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		ie_origem_cliente
	)values (
	        nr_seq_cliente_w,
	        'C',
	        'PA',
	        clock_timestamp(),
	        clock_timestamp(),
	        'P',
	        cd_pessoa_fisica_w,
	        cd_cgc_w,
	        cd_estabelecimento_p,
	        clock_timestamp(),
	        nm_usuario_p,
	        nm_usuario_p,
	        clock_timestamp(),
		'PW');


	insert into pls_comercial_historico(
		nr_sequencia,
		nr_seq_cliente,
		cd_estabelecimento,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nm_usuario_historico,
		ds_titulo,
		ie_tipo_atividade,
		ds_historico,
		dt_historico
	)values (
		nextval('pls_comercial_historico_seq'),
		nr_seq_cliente_w,
		cd_estabelecimento_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nm_usuario_p,
		'Proposta adesão web',
		3,
		'Cliente gerado pela proposta adesão web',
		clock_timestamp());

	insert into pls_solicitacao_vendedor(
		nr_sequencia,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_vendedor_canal,
		nr_seq_cliente,
		nr_seq_vendedor_vinculado
	)values (
		nextval('pls_solicitacao_vendedor_seq'),
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_vendedor_canal_w,
		nr_seq_cliente_w,
		nr_seq_vendedor_vinculado_w );

	update	pls_proposta_adesao
	set	nr_seq_cliente = nr_seq_cliente_w
	where	nr_sequencia = nr_proposta_p;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
